css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
*
  {
  }

element.style {
    border-radius: 5px;
}
 body, html {
     background-color: #4F89AE;
     background-image: url(https://cdn.discordapp.com/attachments/582605365145894922/800737679842082856/background2.png);
     background-position: top;
     background-repeat: repeat-x;
     margin: auto;
     margin-top: auto;
     width: 1200px;
     padding-top: 70px;
    
     }
nav {
    width:1904px;
    overflow:auto;
    margin:-230px 0 0 -352px;
}
body {
     background-color: rgb(255, 255, 255);
     background-image: url('https://cdn.discordapp.com/attachments/582605365145894922/800454687891390504/Banner.png');
     background-size: 100%;
     background-repeat: no-repeat;
     padding-top: 13%;
}
.main-holder {
    padding-top: 17%;
}

nav div.secondary {
    background-color:#212020;
    color: #fff;
    font-size: .6em;
}
nav .info {
    background-color:#212020;
}
.card .content {
    background-color:#FFFFFF
}
input, textarea {
    border:1px solid #C8C9C9;
    background-color:#fff;
}
.card {
    border-radius: 0px;
    box-shadow: 0 0px 0px rgba(255, 255, 255, 0);
    border:1px solid #C8C9C9;
    overflow: hidden;
}
.card .top:not(.absolute).blue {
    border: 1px solid #007ACC;
    background: #007acc;
    background: linear-gradient(rgba(65,156,216,1) 0%, rgba(2,123,204,1) 100%);
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.card .top:not(.absolute).red {
     border: 1px solid #DD1A21;
    background: #DD1A21;
    background: linear-gradient(rgba(254,87,70,1) 0%, rgba(221,26,33,1) 100%);
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.alert.error {
     border: 1px solid #DD1A21;
    background: #DD1A21;
    background: linear-gradient(rgba(254,87,70,1) 0%, rgba(221,26,33,1) 100%);
}
.card .top:not(.absolute).green {
    border: 1px solid #11A304;
    background: #11A304;
    background: linear-gradient(rgba(23,198,10,1) 0%, rgba(17,163,4,1) 100%);
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.card .top:not(.absolute) {
    border: 1px solid #B6B6B6;
    background: #B6B6B6;
    background: linear-gradient(rgba(208,208,208,1) 0%, rgba(182,182,182,1) 100%);
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.card .top:not(.absolute).orange {
    border: 1px solid #D87C00;
    background: #D87C00;
    background: linear-gradient(rgba(243,154,0,1) 0%, rgba(216,124,0,1) 100%);
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
button:not(.plain), .button:not(.plain) {
    border-radius: 0px;
    border: 1px solid #B6B6B6;
    background: #B6B6B6;
    background: linear-gradient(rgba(208,208,208,1) 0%, rgba(182,182,182,1) 100%);
}
.thread-label {
    border: 1px solid #B6B6B6;
    background: #B6B6B6;
    background: linear-gradient(rgba(208,208,208,1) 0%, rgba(182,182,182,1) 100%);
}
button:not(.plain).blue, .button:not(.plain).blue, .thread-label.blue {
    border: 1px solid #007ACC;
    background: #007acc;
    background: linear-gradient(rgba(65,156,216,1) 0%, rgba(2,123,204,1) 100%);
}
button:not(.plain).green, .button:not(.plain).green, button:not(.plain).bucks, .button:not(.plain).bucks, .thread-label.green {
    border: 1px solid #11A304;
    background: #11A304;
    background: linear-gradient(rgba(23,198,10,1) 0%, rgba(17,163,4,1) 100%);;
}
button:not(.plain).orange, .button:not(.plain).orange, button:not(.plain).bits, .button:not(.plain).bits, .thread-label.orange {
    border: 1px solid #D87C00;
    background: #D87C00;
    background: linear-gradient(rgba(243,154,0,1) 0%, rgba(216,124,0,1) 100%);
}
button:not(.plain).red, .button:not(.plain).red, .thread-label.red {
    border: 1px solid #DD1A21;
    background: #DD1A21;
    background: linear-gradient(rgba(254,87,70,1) 0%, rgba(221,26,33,1) 100%);
}
.modal .cancel-button, button:not(.plain) {
    border: 1px solid #B6B6B6;
    background: #B6B6B6;
    background: linear-gradient(rgba(208,208,208,1) 0%, rgba(182,182,182,1) 100%);   
}
.admin-forum-options a, .admin-forum-options button {
    background: transparent;
    border: 0px;
}
.box.item-img.owns, .box.item-img.special {
    border: 1px solid #C8C9C9;
}
.box {
    border-radius: 0px;
}
.owned-check {
    color: transparent;
}
.tabs .tab, .avatar-card.crate .img-holder, .profile-card {
    border-radius: 0px;
}
.side-ad {
    display:none;
}
.card .thumbnail.dark {
    background-color: white;
}
.card .item-card-details, .card .item-content {
    background-color: white;
    border: 0px;
}
.forum-tag {
    background: url(https://cdn.discordapp.com/attachments/582605365145894922/800681990281887755/forumtag.png)round right top;
}
.forum-tag::after {
    border-right-color: #CFCFCF;
}
.select, select, input, textarea {
    border-radius: 0px;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/dashboard/") {
.input-group .input-button {
    color: white;
}
}

@-moz-document regexp("https://www.brick-hill.com/forum/") {
*
  {
  }


.card .top:not(.absolute) {
    border: 1px solid #46BCF9;
    background: #46BCF9;
    background: linear-gradient(rgba(73,208,250,1) 0%, rgba(70,188,249,1) 100%);
    color: #fff !important;
    font-weight: bold;
    font-size: 20px;
}
.forum-links a {
    font-weight: bold;
    color: #232527;
    font-size: 20px;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/trades") {
.item-card-container {
    border: 0px solid #B6B6B6;
    border-radius: 0px;
    box-shadow: 0px 2px 5px rgba(0,0,0,0);
    background-color: #f7f8f9;
}

}

@-moz-document url-prefix("https://www.brick-hill.com/forum/") {
.forum-links a {
    font-weight: bold;
    color: #232527;
    font-size: 20px;
}
}

@-moz-document regexp("https://www.brick-hill.com/shop/") {
*
  {
  }


.card .thumbnail.dark {
    border: 1px solid #C8C9C9;
}
.card {
    border: 0px;
}
.card .creator {
    display:none;
}
}

@-moz-document regexp("https://www.brick-hill.com/customize/") {
.modal .cancel-button, button:not(.plain) {
    background: transparent;
}
input.rigid {
    background-color: white;
}
.item-types a.active {
color: #13B0FE;
}
body {
    font-weight: 600;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/user/") {
*
  {
  }


button:not(.plain).blue, .thread-label.blue {
    background: transparent;
    border-color: white white orange white;
    border-bottom-width: thick;
    color: #232527;
    border-radius: 0px;

}
button.tab-button.transparent, .button.tab-button.transparent {
    background: transparent;
    border-color: white white #C3CDD8 white;
    border-bottom-width: thick;
    color: #232527;
}
.profile-card.crate img {
    border-radius: 0px;
}
.profile-card.award img, .profile-card:not(.crate) img {
    border: 0px solid;
    border-radius: 110px;
}
.crate-types .active {
    background-color: #F57D20;
    border-left: 0px;
    border-right: 10px solid white;
}
.crate-types :hover {
    border-left: 0px;
    border-right: 10px solid white;
}
.crate-types li:first-child {
    border-radius: 0px;
}
body {
    color: #232527;
}
.card .top:not(.absolute).blue, .card .top:not(.absolute).red, .card .top:not(.absolute).green, .card .top:not(.absolute), .card .top:not(.absolute).orange {
    color: #232527 !important;
    border: 0px;
    background: white;
}
button:not(.plain) {
    padding: 10px 122px;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/play/") {
button:not(.plain).blue {
    border: 1px solid #11A304;
    background: #11A304;
    background: linear-gradient(rgba(23,198,10,1) 0%, rgba(17,163,4,1) 100%);
    color: #fff !important;
    font-weight: bold;
    font-size: 30px;
    margin:-180px 0 0 -395px;
    width: 230px;
    height:60px;
}
.box.game-img img {
    border-radius: 0px;
    border: 1px solid #C3CDD8;
}
.card .top:not(.absolute).blue {
    color: #3F4042 !important;
    background: transparent;
    border: 0px;
    font-size: 25px;
}
.mt2 {
    margin:20px 0 0 -395px;
}
.card .content {
    overflow-y: visible;
}
.red-text {
    margin:340px 0 0 -140px;
}
.small-text {
    margin: -600px 0 0 120px;
    font-size: 16px;
    height: 610px;
    display:none;
}
.favorite-text {
    margin: -100px 0 0 -395px;
}
.box.game-img img {
    width: 115%;
}
.item-stats {
    padding-bottom: 3px;
}
}

@-moz-document url-prefix("https://www.brick-hill.com/clan/") {
button.tab-button.transparent {
    color: white;
}
.fieldset {
    border-radius: 0px;
}
.clan-img-holder img, .clan-img-holder {
    border-radius: 110px;
}
.card .top:not(.absolute) {
    background: white;
    border: 0px;
}
.card .top:not(.absolute) {
    color: #232527 !important;
    font-size: 25px;
}
}
