css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
    20091015:
        google.hk move setting to top bar, fix padding
    20090815:
        change match url, it may conflict with other google service
    20090814:
        google.hk change search from google.com.hk/search to google.com.hk/
    20090719:
        @-moz-document suitable site only
*/

/* global */
@-moz-document url-prefix("http://www.google.com.hk/") {

    /* Search input box */
    input[name=q] {
        width: 18em !important;
        font-size: 1.2em !important;
    }

    /* Header bar */
    .gbh {
        /* position: relative !important; */
        top: 1.2em !important;
        font-size: 1em !important;
        /*display: none !important;*/
    }

    /* Header bar */
    #gbar {
        font-size: .8em !important;
    }


}

/* index */
@-moz-document url("http://www.google.com.hk/") {

    /* Logo */
    div[title="Google"]>div {
        font-size: 12px !important;
    }

    /* Search Button */
    td[nowrap][width="25%"] {
        padding-bottom: 2em !important;
    }
    input[name=btnG] {
        font-size: 1.2em !important;
    }
    input[name=btnI] {
        display: none !important;
    }

    /* index only? */
    #guser {
        font-size: .8em !important;
    }

    font[size="-1"] {
        font-size: .9em !important;
    }
}


/* search */
@-moz-document url-prefix("http://www.google.com.hk/") {

    /* search page only? */
    #gb {
        font-size: .8em !important;
    }

    /* search page use font-size: small; ?? */
    html, body {
        font-size: medium !important;
    }

    /**** search page ****/
    /* Search Page - input box */
    table.ts {
        width: 100% !important;
        margin-bottom: .2em !important;
    }
    table.ts td.nobr {
        text-align: right !important;
    }


    /* fix browser width */
    table#sft {
        margin-left: auto !important;
        margin-right: auto !important;
    }
    table#sft tr {
        vertical-align: middle !important;
    }
    table#sft td a#logo {
        margin-right: .5em !important;
    }
    table#sft label {
        font-size: .9em !important;
    }
    table#sft td.nobr {
        font-size: .9em !important;
    }

    /* Search Result bar */
    div#ssb {
        font-size: .8em !important;
    }

    /* search entry */
    div#res {
        line-height: 1.5em !important;
    }
    li.g {
        /*font-size: medium !important;*/
    }
    /* item header */
    li.g h3.r {
        font-size: 1.4em !important;
    }
    /* item desc */
    li.g .s {
        font-size: 1.2em !important;
    }

    /* footer detail */
    div#wml {
        font-size: .9em !important;
    }

    /* google translate */
    td.s {
        font-size: .9em !important;
    }


}
