css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

body { font: 10pt/10pt calibri !important; background: #2C2C2C !important; color: #eee !important; width: 1100px !important; margin: 0 auto !important; }
.boxbar { background: #444 !important; color: #eee !important; }
.box-outer { border: none !important; background: #333 !important; color: #eee !important; }
.box-inner { background: #333 !important; color: #eee !important; }
#recent-images li{ background: #333 !important; }
#header { width: 1100px !important; margin: 0 auto !important; }
#navtop {  margin-top: 10px !important; -moz-border-radius: 5px !important; width: 1100px !important; padding: 5px !important; background: #222 !important; color: #eee !important; text-align: center !important; }
div.logo { position: relative; top: 16px; clear: both; }
.postarea { position: fixed; bottom: 0; right: 0; }
.postblock { border: 0 !important; background: #222 !important; color: #aaa !important; }
.inputtext, textarea, input[type="submit"] { display: block !important; width: 311px !important; }
a { color: #efefef !important; font-weight: bold !important; text-decoration: none !important; border: 0 !important; }
a:hover { color: white !important; background: #222 !important; }
div.logo font { visibility: hidden; }
div.logo font span { visibility: visible; font: bold 40pt/40pt calibri !important; color: white; }
th { background: #222 !important; padding: 10px !important; font-size: small; -moz-border-radius: 5px !important; }
td.reply { width: 1100px !important; border: 0 !important; background-color:#252525 !important; color: #FFF !important; -moz-border-radius:5px !important; clear: both; }
td.replyhl { width: 1100px !important; border: 0 !important; background-color:#4B4B4B !important; color: #eee !important; -moz-border-radius: 5px !important; }
.pages { background: #222 !important; border: 0 !important; clear: both !important; width: 1100px !important; text-align: center !important; }
.pages td, .pages td b { color: #666 !important; }
.postername, .abbr { color: #F00 !important; }
.commentpostername { color: #999 !important; }
a img { float: right !important; clear: both !important; margin: 0 10px 10px 10px !important; }
.deletebuttons input:not([type="checkbox"]) { width: 130px !important; display: inline !important; }
.deletebuttons input[type="checkbox"] { position: relative; top: 3px; }

.yui-g, #filter-button, #option-button, #hd, #ft, #navtopr, hr, td small, .omittedposts, .quotejs, .filesize, .doubledash, #footer, .rules, center font h3, center font small { display: none !important; }

}
