css:
@-moz-document domain("jobsearch.gov.au") {
body {
    color: #c9c9c9;
    background-color: #1c1c1c;
    border: 0px solid #1c1c1c;
}

.navbar ul.contract-picker {
    display: none;
    opacity: 0;
    background: #1c1c1c;
}

.navbar ul.contrast-picker>li.dropdown>a.dropdown-toggle {
    display: none;
    opacity: 0;
}

.navbar ul.contrast-picker>li.dropdown ul.dropdown-menu {
    display: none;
    opacity: 0;
}

.navbar {
    background-color: #1c1c1c;
    border: 1px solid #111;
}

.navbar-brand .crest>img {
    -webkit-filter: invert(50%);
}

.navbar li.cta-jsk>a.btn {
    border: 1px solid #19c371;
}

.navbar li.cta-jsk>a.btn:hover,
.navbar li.cta-jsk>a.btn:focus,
.navbar li.cta-jsk>a.btn:active{
    background: #373737;
    color: #c9c9c9;
    border: 1px solid #19c371
}

.dropdown-menu {
    background-color: #1c1c1c;
    color: #c9c9c9;
    border: 1px solid #111;
    border-radius: 2px;
    -webkit-box-shadow: 0 3px 6px rgba(0,0,0,.15);
    /*-webkit-filter: invert(2%);*/
}

p.link {
    color: #c9c9c9;
}

.fa-user::before {
    -webkit-filter: invert(60%);
}

.fa-envelope-o::before {
    -webkit-filter: invert(60%);
}

.fa-bell-o::before {
    -webkit-filter: invert(60%);
}

.fa-users::before {
    -webkit-filter: invert(60%);
}


.fa.m-badge[data-badge-count]:after,
.icon.m-badge[data-badge-count]:after {
    top: -5px;
    right: -100px;
}

.m-badge[data-badge-count] {
    position: relative;
}


.btn.btn-secondary.cta {
    background-color: #1c1c1c;
}

.container-heading {
    color: #c9c9c9;
    -webkit-filter: invert(0%);
    opacity: 0.75;
}

.alert-info {
    background-color: #1e313a;
    border: 1px solid #111;
}

.alert-warning {
    background-color: #2a2712;
    border: 1px solid #111;
    color: #c0964d;
}

a:link {
    color: #428bca;
}

a:hover {
    color: #80d4f1;
}

.jobsearcheffort-graph {
    border: 2px solid #101010;
    border-radius: 2px;
    margin-bottom: 2px;
}

.find-a-job {
    border: 2px solid #101010;
}

.well {
    background-color: #1c1c1c;
    border: 1px solid #101010;
}

.myjobsearcheffort
.result {
    border: 1px solid #111;
    box-shadow: 0 1px 1px rgba(0,0,0,.05);
    border-bottom: 1px solid #111;
}

.myjobsearcheffort
.result .result-title {
    border-bottom: #111;
    padding: 0 0 2px 0;
}

.myjobsearcheffort
.result:after {
    border-top: 40px solid transparent;
    border-left: 40px solid transparent;
    border-right: 40px solid transparent;
}

.form-control {
    color: #c9c9c9;
    background-color: #3c3c3c;
    border: 1px solid #111;
}

.form-control:hover {
    background-color: #282828;
}

.btn-default {
    color: #c9c9c9;
    background-color: #2c2c2c;
    border: 1px solid #111;
}

.btn-default:hover {
    color: #c9c9c9;
    background-color: #5c5c5c;
    border: 1px solid #111;
}

.k-pager-wrap {
    color: #c9c9c9;
    background-color: #1c1c1c;
    border: 1px solid #111;
}

.k-pager-wrap .k-link {
    background: #6c6c6c;
    border: 1px solid #1c1c1c;
}

.k-pager-wrap .k-link:hover {
    background: #c9c9c9;
    border: 1px solid #111;
}

.k-pager-wrap .k-pager-nav.k-pager-last {
    border-radius: 0 1px 1px 0;
    border: 1px solid #111;
}

.k-pager-wrap>a.k-state-disabled:hover {
    background: #c9c9c9;
}

.k-link:link,
.k-link:visited,
.k-nav-current.k-state-hover,
.k-link {
    color: #428bca;
    border: 1px solid #111;
}

.footer-container {
    background-color: #1c1c1c;
    padding: 10px 10px;
    border: 2px solid #111;
}

.footer-app .app-google {
    width: 28px;
}
.footer-app .app-apple {
    -webkit-filter: invert(50%);
}

.btn.btn-custom.cta, .btn.custom.cta {
    background-color: #c9c9c9;
    border: 1px solid #111;
}
}
