/* ========== Dark Mode ============ */

/* General Attributes */
body, 
html 
{
    background-color: #000 !important;
    color: #fff !important;
}

/* Links */
a {
    color: #fff !important;
    background-color: transparent !important;
}

a:hover{
    color: #30a651 !important;
}

.c-btn--primary:hover{
    color: white !important;
}

/* Header Tools */
.l-header__main {
    background-color: #000 !important;
    border-top: 3px solid #30a651;
    border-bottom: 1px solid #2f3336 !important;
}

.l-header__group {
    border-right: 1px solid #2f3336 !important;
}

.c-group__options {
    background-color: #14181a !important;
}

.c-textbox, .c-textbox:focus {
    color: #d9d9d9 !important;
}

.c-textbox {
    background-color: #000 !important;
    border: 1px solid #2f3336 !important;
    color: #d9d9d9 !important;
}

.c-searchBox.is-active .c-searchBox__search {
    background-color: #000 !important;
    height: 36px !important;
}

.l-headerTools__login {
    border-left: 1px solid #2f3336 !important;
}

/* Header Menu */
.l-menu {
    background-color: #14181a !important;
}

.l-header__nav:after {
    border-bottom: 1px solid #2f3336 !important;
}

.sf-menu li {
    background-color: #14181a !important;
}

.sf-menu li {
    background: #14181a;
}

.sf-menu ul {
    box-shadow: 2px 2px 6px rgba(255,255,255,.2) !important;
}

.sf-menu > li > ul > li, .sf-menu ul ul li {
    border-top: 1px solid #2f3336 !important;
}

.sf-menu ul li {
    background: #14181a !important;
}

.sf-menu a {
    border-left: 0px solid #fff;
    border-top: 0px solid #dFeEFF;
    border-top: 0px solid rgba(255,255,255,.5);
    padding: .75em 1em;
    text-decoration: none;
    zoom: 1;
}

/* Main Content */
main.l-main {
    background-color: #000 !important;
}

.l-content__main {
    border-right: 1px solid #2f3336 !important;
}

.content-side {
    background-color: #000 !important;
}

.c-articleCard__thumbnail,
.c-articleCard__desc
{
    background-color: #14181a !important;
}

.l-selectedList li {
    border-bottom: 1px solid #2f3336 !important;
}

/* Footer */
.l-footer,
.l-footer__copyright
{
    background-color: #000 !important;
}

.l-footer__links {
    background-color: #14181a !important;
    border-bottom: 1px solid #2f3336 !important;
    border-top: 1px solid #2f3336 !important;
}

.l-footer__copyright .list > span > a > img {
    filter: brightness(0.8);
}

/* Page Content */
.c-filter {
    background-color: #14181a !important;
}

.l-listTable__tr {
    border-bottom: 1px solid #2f3336 !important;
}

.l-listTable__thead .l-listTable__tr {
    border-top: 1px solid #2f3336;
}

.l-listTable__thead {
    background-color: #13181A !important;
}

.l-listTable__tbody {
    background-color: #000 !important;
}

.o-fNotes {
    color: gray !important;
}

.l-listTable__tr {
    border-bottom: 1px solid #2f3336 !important;
}

.c-pagination {
    border: 1px solid #2f3336 !important;
}

.c-pagination--prev,
.c-pagination--next {
    border: 0 !important;
}

.l-pagination .is-active a {
    background-color: #17bf63 !important;
    border: 1px solid #17bf63 !important;
    color: #fff !important;
}

.c-btn--primary {
    background: #30A651 !important;
    color: white !important;
}

/* Reply Message */
.c-textEditor__toolBar {
    border-color: #2f3336 !important;
}

.c-dropdown__select>li>span {
    background: #000 !important;
    border: 1px solid #000 !important;
    color: #d9d9d9 !important;
}

.c-dropdown__select ul li.selected, .c-dropdown__select ul li:hover {
    background-color: rgb(23, 191, 99) !important;
    color: rgb(217, 217, 217) !important;
}

.c-dropdown__select ul li {
    background-color: rgb(47, 51, 54) !important;
    color: rgb(217, 217, 217) !important;
}

.c-textarea {
    background-color: #000 !important;
    border: 1px solid #2f3336 !important;
    color: #d9d9d9 !important;
}

.l-helpLink {
    background-color: #14181a !important;
}

.c-iconLink--gn {
    color: #17bf63 !important;
}

hr {
    border-top: 0px solid rgb(47, 51, 54) !important;
}

.c-defaultBlock__desc, .o-fSubMini, .o-fNotes, .o-formDesc {
    color: rgb(217, 217, 217) !important;
}

.c-link--gn {
    color: rgb(23, 191, 99) !important;
}

.c-link--gn:hover
{
    color: white !important;
}

.l-toolBar__item {
    border-right: 0px solid #ECECEC;
}

/* Article */
.l-articlePage__publish blockquote {
    border-left: 3px solid #d9d9d9 !important;
    color: #d9d9d9 !important;
    filter: brightness(0.8) !important;
}

.l-articlePage {
    border-bottom: 1px solid #2f3336 !important;
}

.l-publish__content {
    border-top: 1px solid #2f3336;
}

.o-fSubMini:hover {
    color: #30a651 !important;
}

/* Control Panel */
.c-textStatistics {
    border: 0px solid #2f3336 !important;
}

.l-personalPanel {
    background-color: #14181a !important;
}

.c-panel__item {
    background-color: #14181a !important;
    border: 1px solid #2f3336 !important;
}

.l-searchCard {
    border-bottom: 1px solid #2f3336 !important;
}

.l-listTable__tr {
    border-bottom: 1px solid #2f3336 
}

.l-promptBlock {
    background-color: #14181a !important;
}

.l-listTable__tbody .l-listTable__tr:hover {
	background-color: rgba(213, 220, 230, 0);
	background: linear-gradient(0deg,
			rgba(60, 60, 80, 0) 0,
			rgba(113, 120, 130, .0) 3%,
			rgba(213, 220, 230, .0) 8%,
			rgba(213, 220, 230, .0) 92%,
			rgba(113, 120, 130, .0) 97%,
			rgba(60, 60, 80, .0) 100%);
}

/* Fixed Bottom */
.fixedbottom .dialog {
    background-color: #000 !important;
}

.c-btn--secondary {
    background: #30a651 !important;
    border: 0px solid #30A651 !important;
    color: #fff !important;
}

/* ========== General ============ */

/* Html and Body*/
html,
body,
.l-container,
.l-header,
.l-main,
.l-footer,
.signInPage
{
	min-width: 1250px;
}

.l-footer__copyright {
	width: 1150px !important;
}

.background-side,
.l-footer .l-wrap {
	width: auto !important
}

.l-header__main .l-wrap,
.l-menu .l-wrap,
.background-side .l-wrap
{
    width: auto !important;
}

main.l-main {
    min-height: 100vh !important;
}

/* Font Size */
.l-header__group,
.c-group__link,
.c-searchBox.is-active .c-searchBox__input,
.l-header__tools,
.sf-menu a,
.sf-menu ul li a,
.l-articleCardDesc,
.l-selectedList,
.l-footerLinks>li a,
.o-selectItemTitle,
.c-breadCrumb li a
{
    font-size: 1.1rem;
}

/* Header Group */
.l-header__left {
    margin-left: 1rem;
}

.l-header__tools {
    margin-right: 1.5rem;
}

.c-group__link:hover{
    color: #30a651;
    background-color: transparent !important;
}

.c-group__title>span:before {
    margin-left: -0.5rem;
}

.c-group.is-active .c-group__title:after {
    margin-left: 0.25rem;
}

.c-group__title:after {
    margin-left: 0.25rem;
}

/* Nav Links */
.c-menuLv1{
    padding:0 5rem;
}

.sf-menu a {
    color: #333;
}
    

.sf-menu a:hover {
    color: #30A651;
}

.sf-menu ul {
    min-width: 15em;
}

/* Main Content */
.l-content__main {
    width: calc(100vw - 31vw);
    margin: 0;
    padding: 0;
}

.l-emCardGroup {
    width: 97.5%;
    margin-left: 0.7rem;
    margin-right: 0.7rem;
}

.l-emCardGroup__main 
{
    width: 67.75% !important;
}

.l-emCardGroup__attached 
{
    width: auto;
    padding-left: 0rem;
}
    
.l-emCardGroup__attachedTop
{
    margin-bottom: 0.5rem;
    width: 100%;
}
    
.l-emCardGroup__attachedBottom
{
    width: 100%;
    margin-top: 0rem;
}

.c-mainCard 
{
    width: auto !important;
}

.l-emCardGroup__main .c-mainCard 
{
    
}

.c-mainCard--sm {
    width: auto !important;
}

.l-emCardGroup__main .c-mainCard,
.c-mainCard__thumbnail{
    width: auto;
    height: 410px;
}
    
.c-mainCard__thumbnail .o-thumb{
    background-position: 2% 50% !important;
}

.l-emCardGroup__attached .c-mainCard--sm,
.c-mainCard--sm .c-mainCard__thumbnail{
   height: 200px;
}

.c-articleCard img 
{
    width: 100%;
    height: auto;
}

.o-thumb {
    background-image: url(https://attach.mobile01.com/image/news/265d052….jpg);
    background-size: cover;
    background-repeat: no-repeat;
}

.u-gapNextV--lg + .u-gapNextV--lg {
    width: calc(100% - 3%);
    margin-top: 1rem;
    margin-left: auto;
    margin-right: auto;
}

.l-cols__4 {
    width: 33%;
    padding-top: 0.8rem;
}

.l-selectedThumb {
    width: calc(100vw - 76.5vw);
}

.l-cols__8 {
    width: 65%;
}

.l-tab__container{
    padding-top: 0;
}

.u-gapBottom--max {
    margin-bottom: 0;
}

/* Side Content */
.l-content__side{
    padding-left: 1rem;
    padding-right: 1rem;
    width: calc(100vw - 78vw);
}

.l-content__side .l-tab{
    margin-top: 0rem;
}

/* Page Content */
.l-hotCardGroup li {
	width: calc((100% - 12mm)/5);
	padding: 0;
	margin: 0 0 0 2mm
}

.c-dropdown--sort .c-dropdown__select > li > span{
    padding: 0 15px 0 15px;
}

.l-pagination {
    padding: 0.5rem 1rem;
}

.c-breadCrumb {
    padding: 0 0 0 1rem;
}

.l-heading__fixed {
    padding-right: 1rem;
}

.l-tabulate__nav + .l-tabulate__content {
    margin-top: 0.5rem;
    padding: 0 1rem;
}

/* Sign In Content */
.l-signInPage__content 
{
    padding: 30px 17rem;
    border: 1px solid #ECECEC;
    font-size: 1.2rem;
}

.l-heading__title {
    padding-left: 1rem;
}

.l-formRow,
.l-formRow + .l-formRow,
.l-actions > .c-btn
{
    font-size: 1.2rem;
}

.l-actions {
    margin: 1rem;
    padding-left: 20rem;
}

/* Article */
article {
    font-size: 1.2rem;
    padding: 0 0.5rem;
}

.l-docking,
.l-docking.is-docking{
    display: none;
}

.c-btn {
    padding: 9px 15px;
}

/* Personal Page */
.l-interact {
    padding-left: 1rem;
}

/* Market */
.l-thumbGallery--noGray .l-thumbGallery__item.is-active{
    margin-left: 1.7rem;
}

.u-gapTop--md {
    margin-left: 1.7rem;
}

/* Footer */
.l-footerLinks>li 
{
    margin: 0 auto;
}

