css:
@-moz-document domain("playercounter.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #070707;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #070707;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #4536d1;
        }

h1, h2, h3, h4, h5, h6 {
	color: #bbacac;
}
body {
	background: #000;
	color: #fff;
}
.page-wrap .content-wrapper {
	background: #000;
}
.widget-section.blog-sidebar .widget, .blog-post {
    border-color: #16181a;
    -webkit-box-shadow: 0 0 10px #16181a;
    box-shadow: 0 0 10px #16181a;
}
.breadcrumb {
	color: #fff;
}
a:hover, a:focus {
	color: #bbacac;
}
a, h1 a, h2 a, h3 a, h4 a, h5 a, h6 a, button:hover, input[type="submit"]:hover, input[type="button"]:hover, input[type="reset"]:hover, .to-top:hover, .special .meta-post .fa, .hentry .meta-post a:hover, .special h2.title-post a:hover, .widget-section .widgettitle, .default-testimonials .client-info .client .client-name, .type-team.type-b .team-social li a, .type-team .team-content .name {
    color: #813be6;
}
.wp-caption, .gallery-caption {
	color: #999;
}
.relpost-block-single:hover, .single-post-nav {
	background: #111;
}
.relpost-block-single {
	background: #000;
}
.relpost-block-single-text {
	color: #ccc !important;
}
.footer-widgets, .site-footer {
	border-color: #1b1b1b;
}
.rpwe-block li {
	border-color: #111;
}
.content-area.special .blog-content-wrapper {
	background: #070707;
}
.special h2.title-post a, .nav-links .page-numbers:not(.dots):hover, .nav-links .page-numbers.current {
    color: #b7abec;
}
.widget-section, .widget-section a {
	color: #868686;
}
}
