css:
@-moz-document url-prefix("https://denhulster.magister.net/magister") {
:root {
    --chrome-background: #f00;
    --primary-background: #d800aa;
    --secondary-background: #a70072;
    --background-1: #84118a;
    --background-2: #f00;
    --background-3: #f00;
    --background-4: #434343;
    --background-5: #ff0000;
    --background-6: #333;
    --background-7: #ff0000;
    --background-8: #f00;
    --background-9: #ff000000;
    --background-10: #860032;
    --background-11: #ffffff;
    --primary-text-color: #ff00f8;
    --secondary-text-color: #fff;
}

.menu-footer {
    height: 64px;
    line-height: 64px;
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    border-top: 1px solid rgb(128 0 100);
    text-align: left;
    background: #86005b;
}

.head-bar {
    position: absolute;
    top: 24px;
    right: 18px;
    left: 14px;
    z-index: 680;
    padding-left: 8px;
    height: 47px;
    line-height: 46px;
    color: #ff00c9;
    border-bottom: none;
}

body {
    margin: 0;
    padding: 0;
    height: 100%;
    text-align: center;
    background-color: #710059;
    font-family: tahoma,sans-serif;
    font-size: 11px;
}

.tabs li a {
    display: block;
    text-align: center;
    line-height: 40px;
    height: 40px;
    color: #ff40d6;
}

.tabs {
    height: 41px;
    border-bottom: 2px solid #ff40d6;
    width: 100%;
}

aside .tabs li.active {
    font-weight: bold;
    border-bottom: 2px solid #ff97e9;
}

.head-bar div>a {
    display: block;
    color: #ff40d6;
    height: 47px;
    line-height: 46px;
    color: #ff40d6;
    font-size: 14px;
}

.head-bar div.dropdown-menu>a::after {
    content: "\F107";
    font-family: Font Awesome\ 5 Pro;
    color: #ffb3ef;
    text-align: left;
    font-size: 12px;
    z-index: 300;
    pointer-events: none;
    margin-left: 4px;
    vertical-align: middle;
}

.block h3 {
    position: relative;
    z-index: 100;
    margin: 0;
    padding: 0 0 0 8px;
    font: 14px "droid_sansregular",arial,helvetica,sans-serif;
    color: #710059;
    height: 44px;
    line-height: 44px;
    white-space: nowrap;
    overflow: hidden;
    -moz-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
    border-bottom: 1px solid #ff40d6;
    -moz-box-shadow: 0 2px 5px -1px rgba(0,0,0,0.15);
    -webkit-box-shadow: 0 2px 5px -1px rgba(236,0,255.33);
    box-shadow: 0 2px 5px -1px rgba(255,0,224.37%);
}

form label {
    padding-left: 6px;
    display: block;
    font-weight: bold;
    color: #d800aa;
    padding-bottom: 4px;
}

.k-dropdown .k-input, .k-dropdown .k-state-focused .k-input, .k-menu .k-popup {
    color: #d800aa;
}

.block .content form {
    background-color: #3a062f;
}

.k-dropdown .k-dropdown-wrap.k-state-default {
    background: none;
    background-color: #5f3e58;
    background-position: 50% 50%;
    border: 1px solid #4c1f42;
}

.block {
    position: relative;
    border: 2px solid #ad0088;
    background-color: #ff00c8;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}

.content-container {
    position: relative;
    height: 100%;
    width: 100%;
    text-align: left;
    overflow: auto;
    border: 2px solid #da00ab;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}

.appbar>div>a:visited, a.appbar-button:visited {
    color: #d800aa;
}

.appbar>div>a, a.appbar-button {
    width: 100%;
    height: 100%;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: #d800aa;
    position: relative;
}

.main-menu>li.active>a, .main-menu>li>a:hover {
    background-color: #ff51c8;
    border-radius: 6px;
}

.main-menu li a {
    display: flex;
    align-items: center;
    color: #6b0054;
    text-decoration: none;
    height: 40px;
    margin-bottom: 4px;
    padding: 0;
}

.menu-footer a {
    height: 40px;
    color: #ff00c8;
    text-decoration: none;
    padding-left: 10px;
    margin: 0 12px;
}

.menu-footer>a>i {
    margin: 0;
    transition: 0.2s ease-in-out;
    margin-right: 4px;
    height: 20px;
    width: 20px;
    font-size: 18px;
    padding-top: 2px;
    color: #ff3ab6;
}
.appbar .menu-button>a:hover>span {
    display: block;
    position: absolute;
    left: 44px;
    top: 6px;
    z-index: 10;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 11px;
    color: #781e7b;
    background: #ff00c8;
    border: 1px solid #bd0094;
    box-shadow: 0px 2px 4px 0px rgb(167 0 114);
    border-radius: 4px;
    padding: 4px 8px;
    white-space: nowrap;
}

.appbar>div>a i, .appbar-icon {
    color: #820054;
    font-size: 18px;
}

.column-container h3 {
    height: 41px;
    line-height: 39px;
    font-family: tahoma,sans-serif;
    font-size: 11px;
    color: #800064;
    font-weight: bold;
    background: #ff00c9;
    border-top: 1px solid #b70090;
}

.widget .list li a {
    min-height: 39px;
    line-height: 39px;
    color: #9f0064;
    white-space: nowrap;
    overflow: hidden;
    -moz-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
}

.widget .list li {
    position: relative;
    min-height: 39px;
    line-height: 39px;
    border-top: 1px solid #a70072;
    border-bottom: none;
}

.widget li {
    border-bottom: 1px solid #c7009c;
    min-height: 40px;
    line-height: 40px;
    overflow: hidden;
    background-color: #ff00c9;
}

.sources li ul {
    background-color: #d800aa;
}

.widget .list {
    border-bottom: 1px solid #710059;
}

.sources li a:after {
    display: block;
    position: absolute;
    line-height: 41px;
    font-size: 22px;
    text-align: center;
    left: 10px;
    top: 0;
    font-family: "magistersymbolsnormal";
    color: #a70072;
}

.sm-grid.k-grid .k-grid-content td {
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    position: relative;
    padding: 0;
    border-top: 1px solid #710059;
    border-left: 1px solid #710059;
    color: #710059;
}

.sm-grid.k-grid .k-grid-content tr {
    cursor: pointer;
    background: #ff00c9;
}

span.nrblock {
    display: block;
    float: left;
    margin: 2px 8px 0 0;
    padding: 0 2px !important;
    min-width: 17px;
    height: 16px;
    line-height: 15px;
    text-align: center;
    color: #900054;
    background-color: #ff82e5;
    border-radius: 3px;
    font-family: tahoma,sans-serif;
    font-weight: bold;
    font-size: 11px;
}

#afsprakenlijst-container .today {
    color: #ff00eb;
}

.sm-grid.k-grid .k-grid-content tr.k-state-selected, .sm-grid.k-grid .k-grid-content tr.k-state-selected.k-state-focused, .sm-grid.k-grid .k-grid-content tr.k-state-selected .k-state-focused {
    cursor: pointer;
    background-image: none;
    background: none;
    background-color: #a94b95;
    color: #f00;
}

.agenda-text-icon.outline {
    color: #710059;
    background-color: #710;
    border: 1px solid #c7009d;
    line-height: 18px;
}

.agenda-text-icon[icon-type="information"] {
    background-color: #710059;
    color: #ff00c9;
}

.sm-grid .k-state-selected .more:after {
    content: '...';
    position: absolute;
    left: 50%;
    bottom: 2px;
    font-weight: bold;
    font-size: 18px;
    margin-left: -8px;
    color: #ff00c9;
    line-height: 16px;
}

.sm-grid.k-grid .k-grid-content .k-grouping-row .group-header-content {
    color: #d4b7cd;
    display: block;
    position: relative;
    padding-left: 8px;
    line-height: 31px;
    background-color: #710059;
}

.head-bar div>a>i {
    text-align: center;
    display: inline-block;
    margin-top: 1px;
    color: #710059;
    background: rgb(255 0 200);
    border-radius: 50%;
    width: 40px;
    height: 40px;
    line-height: 40px;
    font-size: 16px;
    font-weight: bold;
}

.block .content .title {
    display: block;
    padding: 0 8px;
    height: 32px;
    line-height: 32px;
    color: #ff00c9;
    font-weight: bold;
    -ms-text-overflow: ellipsis;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
}

dl.list-dl {
    width: 100%;
    overflow: hidden;
    background-color: #3a002d;
}

dl.list-dl dt {
    display: block;
    padding: 0 8px;
    height: 32px;
    line-height: 32px;
    color: #ff00e0;
    white-space: nowrap;
    overflow: hidden;
    -moz-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
    border-top: 1px solid #ff00c9;
    width: 45%;
    font-weight: bold;
}

dl.list-dl dd {
    display: block;
    padding: 0 8px;
    height: 32px;
    line-height: 32px;
    color: #ff00d4;
    white-space: nowrap;
    overflow: hidden;
    -moz-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
    border-top: 1px solid #ff00c9;
    width: 55%;
    border-left: 1px solid #ff00c9;
}

ul, ul li, dl, dt, dd, sup, sub {
    margin: 0;
    padding: 0;
    list-style: none;
    color: #4e003e;
}

.widget .agenda-list li.alert span.time:before {
    color: #540042;
    content: "Aids";
}

.widget .agenda-list li.alert span.time:after {
    position: absolute;
    top: 7px;
    right: 8px;
    font-family: "magistersymbolsnormal";
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    speak: none;
    display: block;
    text-decoration: inherit;
    overflow: hidden;
    text-align: center;
    font-variant: normal;
    text-transform: none;
    content: 'Z';
    color: #840067 !important;
}

.alert {
    border-left: 4px solid #ff0000;
    background-color: #ff00c8 !important;
}

.block h4 {
    position: relative;
    z-index: 1;
    height: 32px;
    line-height: 30px;
    padding: 0 8px;
    color: #710059;
    font-size: 11px;
    font-weight: bold;
    border-top: 1px solid #ff00c9;
    border-bottom: 1px solid #ff00c9;
    -moz-box-shadow: 0 2px 5px -1px rgba(0,0,0,0.15);
    -webkit-box-shadow: 0 2px 5px -1px rgb(255 0 201);
    box-shadow: 0 2px 5px -1px rgb(255 0 201);
}

.block .content {
    position: relative;
    font: 11px tahoma,sans-serif;
    min-height: 30px;
    height: 145px;
    background-color: #ff00c8;
    overflow: auto;
}

.endlink {
    display: block;
    clear: left;
    height: 40px;
    line-height: 40px;
    border-top: 1px solid #710059;
    text-transform: lowercase;
    overflow: hidden;
    background-color: #b70090;
    position: relative;
    -moz-box-shadow: 0 -2px 5px -1px rgba(0,0,0,0.15);
    -webkit-box-shadow: 0 -2px 5px -1px rgb(113 0 89);
    box-shadow: 0 -2px 5px -1px rgb(113 0 89);
}

.widget .list li.no-data:hover, .widget .list li.no-data a:hover {
    background-color: #710059;
    cursor: default;
}

.no-data a {
    color: #61004c !important;
    font-style: italic !important;
    font-weight: normal !important;
}

.endlink a {
    display: block;
    float: right;
    padding: 0 8px;
    height: 40px;
    line-height: 40px;
    color: #4a003a;
    text-decoration: underline;
}

#vandaag-container .grade-widget ul {
    background-color: #ff00c8;
    border-bottom: 1px solid #b5008e;
    min-height: 98px;
}

#cijfers-leerling .last-grade {
    float: left;
    padding: 11px 0 0 0;
    margin: 8px 0 8px 8px;
    height: 80px;
    width: 80px;
    text-align: center;
    color: #710059;
    background-color: #ff41d6;
    font-size: 36px;
    font-family: "droid_sansregular",arial,helvetica,sans-serif;
    border-radius: 5px;
}

p.empty-message {
    color: #5f0000 !important;
    text-align: center;
    margin-top: 12px;
}
}
