css:
@-moz-document domain("forums.ebay.co.uk"),
               domain("forums.ebay.com"),
               domain("forums.ebay.de"),
               domain("forums.ebay.com.au"),
               domain("forums.ebay.ca") {

body { background: #C2C3EF !important; }

.paginator { background: #afafaf !important; }

.jive-portlet-box { display: none !important; }

.poweredByLiveworld { display: none !important; } 

.pnav { position:absolute !important; left:100px !important; top:0px !important; width:30% !important; }

.srchBox  { background: #000080 !important; }

.srchBoxW { background: #000080 !important; }

a[href*="search/AdvSearch?sofindtype=13"] { color: #FFFFFF !important; font-weight: bolder !important; }

.snav { display: none !important; }

.brnd1 { display: none !important; }
.brnd2 { display: none !important; }
.brnd3 { display: none !important; }
.brnd4 { display: none !important; }
.brnd5 { display: none !important; }
.brnd6 { display: none !important; }
.brnd7 { display: none !important; }


.ebayTitle {
background: #000080 !important;
border-bottom: 1px solid #4444CC !important;
border-top: 1px solid #4444CC !important;
color: #ffffff !important;
text-indent: 25px !important;
}

[src*="imgNotch_13x3.gif"] { display: none !important;
}

[src*="http://pics.ebaystatic.com/aw/pics/navbar/topRight_12x12.gif"]  { display: none !important;
}

[src*="http://pics.ebaystatic.com/aw/pics/navbar/topLeft_12x12.gif"]  { display: none !important;
}

[src*="http://pics.ebaystatic.com/aw/pics/navbar/poweredByLogo_112x22.gif"] { display: none !important; }

[src*="images/blank.gif"] { display: none !important; }

.nomargin + table[width="100%"], [cellspacing="0"],[cellpadding="0"],[border="0"] > span  { background: #C2C3EF !important; }

.jive-even { background: #eeeeee !important; }

.jive-odd { background: #c2c3ef !important; }


.ebayTableTitle { background: #AFAFAF !important;
border: 0px !important;
}

.ebayWatches { background: #AFAFAF !important;
border: 0px !important; 
}

.ebayWelcome { background: #AFAFAF !important;
border: 0px !important; 
}

.ebayTableLinks { background: #AFAFAF !important;
border-top: 1px #4444CC !important; 
}

.RTRemarQPanel1TD { background: #8F8F8F !important; }

.ebayThreadHeader { background: #afafaf !important; }

.ebayRootUserRow { background: #afafaf !important; }

.ebayUserRow { background: #afafaf !important; }

.ebayRootMessage { border: #afafaf !important; }

/* Little something for Ethel */

a[href*="contactUser.jspa?requested=etheleneglycol"]:hover { color: #FF000A !important; text-decoration: blink !important; font-weight: bolder !important; }

a[href*="contactUser.jspa?requested=etheleneglycol"] { color: #CF00A4 !important; font-weight: bolder !important; }


/* Remove right-hand colum to widen forums */

td[width="200"] { display: none !important;
}

}

@-moz-document domain("forums.ebay.co.uk")
{
.srchBox { position:absolute !important; right:1px !important; top:0px !important; width: 40% !important; }
}
