css:
@-moz-document url-prefix("https://boop.pl") {
/*Główna strona*/


.g1-row-padding-m {
    padding-top: 20px;
	background: #031126;
}

.g1-row-layout-page>.g1-row-background {
    background-color: #031126;
}

.home-side, .home-hltd {
 background: #041B3D;   
 padding-top: 10px;

}





.g1-hb-row-a .g1-row-background {
    border-bottom: 1px solid #fff;
    border-color: #fff;
    border-color: var(--g1-hba-border-color);

    background: radial-gradient(circle, #1C66A6, #031126)
}


.g1-hb-row-b .g1-row-background {
  
 background: radial-gradient(circle, #1C66A6, #031126);
}

.g1-body-inner .g1-secondary-nav-menu>.menu-item>a {
    font-weight: 400;
    font-family: rubik,sans-serif;
	color: #FFF;
}

.g1-body-inner .g1-secondary-nav-menu>.menu-item>a:hover {
    font-weight: 400;
    font-family: rubik,sans-serif;
	color: #19D9FF;
}

.g1-body-inner .g1-secondary-nav-menu>.menu-item>a:active {
    font-weight: 400;
    font-family: rubik,sans-serif;
	color: #E06016;
}

.g1-primary-nav-menu>.menu-item {
    display: block;
    font-size: 1rem;
    text-align: left;
	
}

.sub-menu{

background: #000;

}



.g1-body-inner .g1-primary-nav-menu > .menu-item > a, .g1-primary-nav-menu > .menu-item > a:hover {
    font-size: 15px;
    font-weight: 500;
    font-family: rubik, sans-serif;
	color: #FFF;

}

.g1-body-inner .g1-primary-nav-menu > .menu-item > a, .g1-primary-nav-menu > .menu-item > a:active {
    font-size: 15px;
    font-weight: 500;
    font-family: rubik, sans-serif;
	color: #19D9FF;

}




/*napisy na miniartykułach*/

.home-secondary .g1-widget-posts .g1-delta {
    text-transform: uppercase;
	color: #FFF;
}


a, .entry-title > a:hover, .entry-meta a:hover, .menu-item > a:hover, .current-menu-item > a, .mtm-drop-expanded > a, .g1-nav-single-prev > a > span::before, .g1-nav-single-next > a > span::after, .g1-nav-single-prev > a:hover > strong, .g1-nav-single-prev > a:hover > span, .g1-nav-single-next > a:hover > strong, .g1-nav-single-next > a:hover > span, .mashsb-count, .archive-title::before, .snax .snax-item-title > a:hover, .woocommerce .star-rating span::before, .woocommerce-page .star-rating span:hover {
    color: #19D9FF;
	transition: 0.5s;
}


a {

color: #FFF;

}







.g1-gamma, h3 {
    font: 500 19px/1.125 serif;
    font-family: Roboto,Arial,sans-serif;
    letter-spacing: 0;
    text-transform: none;
  color: #FFF;
}



.g1-epsilon, .item-title, h5 {
    font: 500 15px/18px serif;
    font-family: Roboto,Arial,sans-serif;
    letter-spacing: normal;
    text-transform: none;
    color: #FFF;
}



.g1-beta, .entry-content h2, h2 {
    font-weight: 500;
    font-family: rubik, sans-serif;
	 color: #FFF;
}

.g1-alpha-2nd, .g1-beta-2nd, .g1-delta-2nd, .g1-epsilon-2nd, .g1-gamma-2nd, .g1-giga-2nd, .g1-mega-2nd, .g1-zeta-2nd {
    font-family: Roboto,Arial,sans-serif;
    font-weight: 700;
    text-transform: none;
    letter-spacing: normal;
	color: #FFF;
}







.entry-category {
    border-color: #0000;
    background-color: #0000;
	color: #12A0FF;
}



input[type="submit"], input[type="reset"], input[type="button"], button, .g1-button-solid, .g1-button-solid:hover, .g1-arrow-solid, .entry-categories .entry-category:hover, .author-link, .author-info .author-link, .g1-box-icon, .g1-collection-ticker .g1-collection-title, .wyr-reaction:hover .wyr-reaction-button, .wyr-reaction-voted .wyr-reaction-button, .wyr-reaction .wyr-reaction-bar {
    border-color: none;
    background: #1C66A6;
    color: #fff;
}





.entry-date {
    display: inline-block;
    margin-right: 10px;
    vertical-align: top;
	color:#DBDBDB;
}







/*wyróżniony artykuł (dziwny pasek)*/




.g1-tessellated {
    background: #12A0FF;
}

.g1-skinmode .g1-tessellated {
    background: #12A0FF;
}




/*Newsy, Tagi*/




.page-header>.g1-row-background {
    border-width: 0 0 2.5px;
    border-style: solid;
    border-color: #12A0FF;

}






.g1-pagination-item .g1-link {
	background: #12A0FF;
	color: #FFF;
    display: inline-block;
    padding: .25rem 10px;
}


/*CopyRight*/


.g1-footer-text {
    color: #12A0FF;
	opacity: 0.8;
}



.g1-footer-nav a {
    display: block;
    padding: 0 10px;
    color: #FFF;
}


.g1-mega {
    font-family: Rubik,Helvetica,Arial,sans-serif;
	color: #FFF;
}


.entry-content ol, .entry-content p, .entry-content pre, .entry-content table, .entry-content ul {
    font: 400 17px/1.45em Rubik,Helvetica,Arial,sans-serif;
	color: #FFF;
}
hr {
    border-width: 1px 0 0;
    margin: 1.5em 0;
    border-style: solid;
    opacity: 1;
	color: #12A0FF;
}






.g1-back-to-top {
    display: block;
    visibility: hidden;
    width: 28px;
    height: 28px;
    border-width: 1px;
    overflow: hidden;
    position: fixed;
    right: 10px;
    left: auto;
    bottom: 10px;
    z-index: 999;
    text-indent: 100%;
    white-space: nowrap;
    border-style: solid;
    border-color: #0000;
    background-color: #12A0FF;
    color: #fff;
    opacity: 0.76;
    border-radius: 2px;
    transition: opacity 375ms ease-in-out;
}



.g1-back-to-top:hover {
    background-color: #19D9FF;
	transition: 0.5s;
    color: #fff;
    opacity: 0.75;
}






.g1-th, th {
    padding: 10px 5px;
    border-width: 0 0 1px;
    font-size: 13px;
    font-family: Roboto,Arial,sans-serif;
    font-weight: 700;
    text-transform: uppercase;
    text-align: left;
    vertical-align: top;
    border-style: solid;
    border-color: #e6e6e6;
    color: #FFF;
    border-color: #12A0FF;
}



.entry-tag {
    display: inline-block;
    margin-right: 8px;
    font-size: 14px;
    font-family: Roboto,Arial,sans-serif;
    letter-spacing: .05em;
    font-weight: 400;
    vertical-align: top;
    text-transform: uppercase;
    color: #12A0FF;
}


.fc27db8 .ctaButton {
    background-color: #12A0Ff;
    margin-left: 10px;
    position: absolute;
    right: 0;
    top: 0;
}



/*cytaty*/

blockquote {
    font: 400 21px/25px sans-serif;
    text-align: left;
    padding-left: 1em;
    padding-right: 1em;
    border-left: 5px solid #009aff;
    padding: 0 1em;
}



/*dropdown*/


.g1-hb-row .sub-menu {
    background-color: #24609a;
}


.g1-hb-row .sub-menu .menu-item>a {
    color: #00dcff;
}

.g1-hb-row .g1-link-toggle {
    color: #01ffff;
}


a{
color: #12A0FF;

}











/* Scrollbar */
 body::-webkit-scrollbar {
    width: 15px !important;
    height: 15px !important;
    display: block !important;
    background: #03142d !important;
    background-image: url(https://cdn.discordapp.com/attachments/648916305998774301/793843800186552340/image_2.png), url(https://cdn.discordapp.com/attachments/648916305998774301/793843800186552340/image_2.png) !important;
    background-position: top center, bottom center !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
}
/* Thumb */
 body::-webkit-scrollbar-thumb {
    background: linear-gradient(-45deg, #12A0FF, #19D9FF) !important;
    border-left: 2px solid !important;
    border-right: 2px solid !important;
    border-color: #141414 !important;
	border-radius: 10px;
    
}
 body::-webkit-scrollbar-thumb:hover {
    transition: 1.5s;
    background: linear-gradient(-45deg, #0080DD, #06A6CC) !important;
    box-shadow: 0 0 10px linear-gradient(-45deg, #12A0FF, #BB00BB);
}
 body::-webkit-scrollbar-thumb:active {
    background: linear-gradient(-45deg, #A80505, #D6710D) !important;
    box-shadow: 0 0 10px linear-gradient(-45deg, #FF0000, #FFFF00);
}
/* Buttons */
 body::-webkit-scrollbar-button:single-button {
    background-position: center center;
    background-size: contain;
    display: block;
    height: 16px;
    width: 16px;
    background: #14141488;
}
 body::-webkit-scrollbar-button:single-button:hover {
    background: #14141444;
}
body::-webkit-scrollbar-button:single-button:active {
    background: #14141422;
}









.search-form .search-field {
    width: 100%;
    padding-right: 40px;
    border: solid #12A0FF;
}



[type=email], [type=number], [type=password], [type=reset], [type=search], [type=tel], [type=text], [type=url] {
    height: 36px;
    color: #12A0FF;
}








.g1-dropable .g1-drop-with-anim.g1-drop-expanded>.g1-drop-content, .g1-dropable .g1-drop-with-anim.g1-drop-expanded>.g1-drop-toggle>.g1-drop-toggle-arrow, .g1-hoverable .g1-dropable .g1-drop-with-anim:hover>.g1-drop-content, .g1-hoverable .g1-dropable .g1-drop-with-anim:hover>.g1-drop-toggle>.g1-drop-toggle-arrow {
    visibility: visible;
    opacity: 1;
    -webkit-transform: translate(0,0);
    -moz-transform: translate(0,0);
    -ms-transform: translate(0,0);
    -o-transform: translate(0,0);
    transform: translate(0,0);
    transition: visibility 0s ease-in-out 175ms,opacity 375ms ease-in-out 175ms,transform 375ms ease-in-out 175ms;
    background: #24609a;
}









}
