css:
@-moz-document domain("headphonesty.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #000;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #000;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #777;
        }

body {
	color: #bbb;
	background: #000;
}
.content {
	color: #bbb;
}
.content figcaption {
	color: #505050;
}
.wp-caption-text:before, .post-categories:before {
	background: #1c1e1f;
}
.widget+.widget, .post-author, .post-archive-related, .navbar-nav .sub-menu, .post-archive-standard+[class*="post-archive"], .content p>a {
	border-color: #1c1e1f;
}
.content p > a:focus, .content p>a:hover {
	background: #1c1e1f;
	color: #fff;
}
.navbar-nav .sub-menu {
	background: #000;
}
.content>h2 {
	border-color: #fff;
}
h2, .page-header-archive h1, .post-featured h2, a, .content .lead, h1 a, h2 a, h3 a, h4 a, h5 a, h6 a, h3, .h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6, .read-full-review, .highlight-link, .title-widget, .title-trending, .title-related, .title-comments, .comment-reply-title, .nav-links, .list-categories, h2.entry-title a {
	color: #fff;
}
h1 a:hover, h2 a:hover, h3 a:hover, h4 a:hover, h5 a:hover, h6 a:hover, a:hover, a:active, a:focus, a:hover:active, a:focus:active {
	color: #777;
}
::selection {
    background: #deeff4;
    color: #000;
}
.alert-info {
    background-color: #292e31;
    border-color: #4c5a62;
    color: #56b6e6;
}
.alert-warning {
    background-color: #0f0d05;
    border-color: #110f06;
    color: #eebc67;
}
.content .block-border-left {
	border-color: #fff;
}
.content>ol>li::before {
	color: #fff;
	border-color: #fff;
}
.content ol li:hover::before {
	background: #fff;
	color: #000;
}
.post-author h4:after, .title-related:after, .title-comments:after, .comment-reply-title:after, .post-standard:not(.post-featured) h2:after {
	background: #fff;
}
.owl-dot span {
	background: #505050;
}
.post-comments {
	background: #080808;
}
.post-meta, label, .post-categories, .widget-about-lead, .share-title, .post-count, .sub-title, .comment-metadata, blockquote cite, .post-tags, .tagcloud, .timestamp, #wp-calendar caption, .logged-in-as, .navbar-secondary .navbar-nav>li.menu-item>a {
	color: #505050;
}
.form-control {
    color: #888;
    background-color: #000 !important;
    border-color: rgba(255, 255, 255, .15);
}
.form-control:active, .form-control:focus {
	background: #000 !important;
	color: #888 !important;
	border-color: #999 !important;
}
[src='https://www.headphonesty.com/wp-content/uploads/2017/02/logo.png'] {
	filter: invert(100);
}
.navbar-primary .navbar-nav > li.menu-item > a:focus, .navbar-primary .navbar-nav>li.menu-item>a:hover {
	color: #888;
}
.navbar-primary .navbar-nav>li.menu-item>a, .navbar-secondary .navbar-nav > li.menu-item > a:focus, .navbar-secondary .navbar-nav>li.menu-item>a:hover, .content blockquote, .post-share-horizontal .share-buttons a:focus, .post-share-horizontal .share-buttons a:hover, .header-btn-search {
	color: #fff;
}
.navbar-primary, .navbar-secondary {
    border-color: rgba(22, 22, 22, .7);
}
.navbar-nav > li.menu-item li.menu-item > a:focus, .navbar-nav > li.menu-item li.menu-item>a:hover, h2.entry-title a:hover {
	background: #fff;
	color: #000;
}
.navbar-nav > li.menu-item li.menu-item>a {
	color: #fff;
	border-color: #1c1e1f;
}
.header-navbar-stuck {
	background: #000;
	border-color: #000;
}
.navigation.pagination .nav-links {
	color: #505050;
	border-color: #fff;
}
.tablepress .row-hover tr:hover td {
	background: #030303;
}
.tablepress .even td {
	background: #000;
}
.tablepress tbody td, .tablepress tfoot th, .tablepress thead th {
	border-color: #222;
}
.tablepress .odd td {
	background: #090909;
}
.tablepress tfoot th, .tablepress thead th {
	background: #fff;
	color: #000;
}
#search {
	background: #000;
}
::placeholder {
	color: #fff !important;
}
#search p {
	color: #505050;
}
#search .close, .close:focus, .close:hover, .post-standard h2 {
	color: #fff;
}
.post-share-horizontal .title-share, .post-share-horizontal .share-buttons a {
	color: #4a4a4a;
}
.alert-success {
    background-color: #1d3712;
    border-color: #15260d;
    color: #84ff86;
}
[src='https://www.headphonesty.com/wp-content/uploads/2018/08/Blue-headphone-with-wave-big-300x300.jpg'] {
	filter: invert(100);
}
.elementor-widget-heading .elementor-heading-title {
	color: #fcfcfc !important;
}
.pt-cv-title a, .pt-cv-wrapper .pagination>li>a, .pt-cv-wrapper .pagination>li>span, .page-header h1, .post-header h1 {
	color: #fff;
}
.pt-cv-content {
	color: #ddd;
}
.pt-cv-pagination-wrapper {
	color: #505050;
	border-color: #fff;
}
.pt-cv-wrapper .pagination>li>a:focus, .pt-cv-wrapper .pagination>li>a:hover, .pt-cv-wrapper .pagination>li>span:focus, .pt-cv-wrapper .pagination>li>span:hover {
	background: #111;
	border-color: #222;
	color: #3e95e3;
}
.pt-cv-wrapper .pagination>li>a, .pt-cv-wrapper .pagination>li>span {
	background: #000;
}
.elementor-widget-text-editor {
	color: #8a8a8a;
}
.page-header-standard h1:after {
	background: #fff;
}
}
