css:
@-moz-document domain("youtube.com") {
/* ПалитРА */
:root {
    --mainbg: /*[[mainbg]]*/;
    --maina: /*[[bg]]*/99;
    --mainaa: /*[[bg]]*/33;
    --mtext: #CCC;
    --blacka: #0008;
    --blackaa: #0003;
    --white: #fff;
    --whitea: #fff8;
    --whiteaa: #fff2;
}
#page-manager.ytd-app {
  background-color: var(--mainbg);
}
.masthead-finish {
  background-color: var(--maina);
  backdrop-filter: blur(20px);
  filter: drop-shadow(0.35rem 0.35rem 0.4rem rgba(0, 0, 0, 0.5));
}
#logo-icon > svg > g > g:nth-child(1) > path {
    fill: var(--mainbg)!important;
}
.guide-icon.ytd-guide-entry-renderer {
    color: var(--mainbg);
}
.ytd-guide-renderer {
  background-color: transparent !important;
}
#contentContainer.app-drawer[opened] {
  background-color: var(--maina);
  backdrop-filter: blur(20px);
  filter: drop-shadow(0.35rem 0.35rem 0.4rem rgba(0, 0, 0, 0.5));
}
.gsfs {
    color: var(--mtext);
}

#guide-content.ytd-app {
  background: var(--maina) !important;
  background-color: var(--maina) !important;
}
#container.ytd-searchbox {
  background-color: #00000060;
  border-radius: 25px 0px 0px 25px;
  border: none;
  padding-left: 15px;
}
#search-icon-legacy.ytd-searchbox {
  background-color: var(--blacka);
  border-radius: 0px 25px 25px 0px;
  border: none;
}
.sbdd_a .sbdd_b {
  background: none;
  box-shadow: none;
  border: none;
}
.sbsb_a {
  background: none;
  background-color: var(--maina);
  border: none;
  border-radius: 0px 0px 15px 15px;
  backdrop-filter: blur(20px);
  filter: drop-shadow(0.35rem 0.35rem 0.4rem rgba(0, 0, 0, 0.5));
}
.sbsb_d {
  background-color: var(--blackaa);
}
.sbsb_i {
  padding-left: 8px;
  margin-right: -10px;
  color: var(--mtext);
}
.sbpqs_a {
  padding-left: 8px;
  color: var(--mtext) !important;
}
.sbqs_c {
  padding-left: 8px;
  color: var(--mtext) !important;
}
.sbfl_a {
  color: var(--blacka);
  padding-right: 8px;
}
.sbfl_a:hover {
  color: var(--blackaa);
}
.sbfl_b {
  background-color: transparent;
}
#search-icon-legacy.ytd-searchbox yt-icon.ytd-searchbox {
    color: #FFF;
}
#scrim.visible.tp-yt-app-drawer {
  backdrop-filter: blur(15px);
  transition: all 300ms ease-in;
}
ytd-app {
    --app-drawer-content-container_-_background-color: var(--maina)!important;
    background: var(--maina);
}
ytd-masthead {
    background: var(--maina)!important;
    background-color: var(--maina)!important;
}
#container.ytd-searchbox {
    box-shadow: inset 0 1px 2px var(--maina);
}
#search-icon-legacy.ytd-searchbox:hover {
    border-color: var(--blackaa);
    background-color: var(--blackaa);
    box-shadow: 0 1px 0 rgb(0 0 0 / 10%);
}
yt-icon {
    fill: var(--mainbg);
}
ytd-mini-guide-renderer {
    background-color: var(--maina);
}
ytd-mini-guide-entry-renderer {
    background-color: transparent;
}
.title.ytd-mini-guide-entry-renderer {
    color: var(--mainbg);
}
#container.ytd-searchbox input.ytd-searchbox {
    color: var(--mainbg);
}
#video-title.ytd-rich-grid-media {
    color: var(--mtext);    
}
#guide-links-primary.ytd-guide-renderer > a, #guide-links-secondary.ytd-guide-renderer > a, #footer.ytd-guide-renderer > #copyright {
    color: var(--mtext);
}
#country-code.ytd-topbar-logo-renderer, #guide-section-title.ytd-guide-section-renderer {
    color: var(--mtext);
}
ytd-mini-guide-entry-renderer[active] .title.ytd-mini-guide-entry-renderer {
    color: var(--mtext);
}
ytd-multi-page-menu-renderer {
    backdrop-filter: blur(15px);
    background: var(--maina);
    border: 1px solid var(--maina);
}
ytd-active-account-header-renderer {
    background-color: transparent;
    border-bottom: 1px solid var(--maina);
}
}
