css:
@-moz-document domain("flipanim.com")
{
/* Top & Bottom of page */
  body
  {
    background-color: #330425 !important;
  }

/* Center of page */
  .main
  {
    background-color: #330425 !important;
  }

/* Canvas shadow */
  .animDiv
  {
    box-shadow: 0 0 10px #000000 !important;
  }

/* Pallete */
  .popup_window
  {
    background-color: #330425 !important;
    box-shadow: 0 0 5px #330425 !important;
  }

  .btn_option:hover
  {
    background-color: #330425 !important;
  }

  .btn_option_selected
  {
    background-color: #330425 !important;
  }

/* Loading */
  .anim_view
  {
    box-shadow: 0 0 10px #000000 !important;
    background-color: rgba(175, 175, 175, 0.5) !important;
  }

/* Login & Register */
  .inputFormGroup
  {
    background-color: #330425 !important;
  }
}
