css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/browser.xul") {
  #navigator-toolbox, #toolbar-menubar, #nav-bar, #PersonalToolbar {
    -moz-appearance: none !important;
  }
  #toolbar-menubar {
    padding: 5px 5px 3px;
    border: 0 !important;
  }
  #main-menubar > menu {
    -moz-appearance: none !important;
    padding: 0px !important;
  }
  #main-menubar > menu:hover {
    background: #39f !important;
    color: #fff !important;
  }
  #nav-bar {
    padding: 0 5px 3px;
    border: 0 !important;
  }
  #PersonalToolbar {
    display: none;
  }
  #identity-box {
    -moz-border-radius: 0 !important;
    border-right: none !important;
    background-image: none !important;
    background-color: #fff !important;
    margin-left: -1px !important;
    margin-right: -3px !important;
    border-left: 1px #a3a7ac solid !important;
  }
  #identity-box.verifiedDomain,
  #identity-box.verifiedIdentity {
    background-image: url(chrome://browser/skin/navbar-textbox-buttons.png) !important;
    padding-right: 5px !important;
    margin-right: 1px !important;
  }
  .searchbar-engine-button {
    background-color: #ffffff !important;
    background-image: none !important;
    border: none !important;
  }
  #navigator-toolbox {
    margin: 0 0 0 0 !important;
    background-image: url(data:image/png;base64,%3D);
    border: none !important;
  }
  .tabs-container {
    background-image: url(data:image/png;base64,);
    background-position: bottom;
    padding-left: 7px !important;
  }
  #urlbar, .searchbar-textbox {
    height: 24px;
  }
  .tabbrowser-strip {
    height: 32px;
    -moz-appearance: none;
  }
  .tabbrowser-tab,
  .tabbrowser-tab[selected="true"],
  .tabs-newtab-button,
  .tabs-alltabs-button {
    -moz-border-radius-topleft: 5px !important;
    -moz-border-radius-topright: 5px !important;
    -moz-border-right-colors: #a3a7ac #fff !important;
    -moz-border-left-colors: #a3a7ac #fff !important;
    -moz-border-top-colors: #a3a7ac #fff !important;
    border-width: 2px !important;
    border-style: inset;     background: none !important;
    -moz-appearance: none !important;
    padding-top: 0 !important;
    padding-bottom: 1px !important;
    padding-left: 0 !important;
    margin-top: 2px !important;
    margin-bottom: 5px !important;
    margin-left: -1px !important;
  }
  .tabbrowser-tab:first-child {
     margin-left: 0 !important;
  }
  .tabbrowser-tab:hover,
  .tabs-newtab-button:hover,
  .tabs-alltabs-button {
     background-image: url(data:image/png;base64,%3D) !important;
     background-color: transparent !important;
     background-repeat: repeat-x !important;
  }
  .tabs-newtab-button:hover,
  .tabs-alltabs-button {
     background-image: url(data:image/png;base64,%3D%3D) !important;
  }
  .tabbrowser-tab[selected="true"] {
     background-image: url(data:image/png;base64,) !important;
     font-weight: normal !important;
     margin-top: 1px !important;
     margin-bottom: 4px !important;
  }
  .tabs-alltabs-button {
    -moz-border-radius-topright: 0 !important;
    -moz-border-radius-topleft: 0 !important;
  }
  .tabs-alltabs-button:hover {
     background-image: url(data:image/png;base64,%3D) !important;
  }
  .tab-close-button {
     margin-bottom: 2px !important;
  }
  .tab-icon-image {
     margin-right: 5px !important;
  }
}
