css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") {

body {background: #333 url("data:image/jpg;base64,") repeat !important;

	color: #ddd !important;
}

a:link { color: #82BDFF !important; text-decoration: none !important; }
a:visited { color: #33FFFF !important; }
a:hover { color: #ff6600 !important; }
:focus { outline: none; }
.eLink { border-bottom: 1px solid #666 !important; }

span, div, p, li, td { color: #ddd !important; }
input *, textarea * { color: -moz-fieldtext !important; }
.tooltip-wrapper-box * { color: #000 !important; }
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c { background: #fff7df; color: #008000 !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d { background: #fff7df; color: #696969 !important; }
.google-ac-e td, .google-ac-e td span { background: #fff7df; color: #8a2be2 !important; }

h1, h2, h3, h4, h5, h6, .hpBlockHeadingGray, .name, .title, #vidTitle, .wsHeading, .header, #catIndexGridHeading, #footer .links th {

	color: #fff !important;}

main-nav, .background {
color: #ddd
}

blockquote {
background-color: #333 !important;

}




}
