@-moz-document domain("quire.io") {

.board-column-body.content, .add-task-bar{
    background-color: #e8e7e7 !important;
}

.add-task-btn{
    color: #313131 !important;
}

.column-name{
    color: #d9d8d8 !important;
}

.display-hint #task-board .list-card-item *{
    color: #4a4a4a !important;
}

.board-column-header{
    background-color: #666 !important;        
}

.board .board-column-body, .board .add-task-bar, .board .add-task-bar .list-card-item::before{
    background-color: #fff0;
}

.board .board-column-header, .board .board-column-body {
    width: 280px;
}

.st.list-card-item.pm.hide-icons.bc:last-child {
    margin-bottom: 4em !important;
}

