css:
@-moz-document domain("digitalelesstof.nl") {
body {
    background-color: #2C2F33 !important;
    color: white !important;
}

.panel {
    background-color: #2C2F33 !important;
}

.modal-content {
    background-color: #2C2F33 !important;
}

.textarea-grey {
    background-color: #23272A !important;
}

input[disabled].textarea-grey {
    background-color: #23272A !important;
}

.grey-text, .grey_text {
    color: #dcddde !important;
}


.white-border {
    border: 2px solid #333333 !important;
}

.panel-default {
    border-color: black !important;
}

.modules-rectangle {
    border: 1px solid #2C2F33 !important;
}

.white-text {
    color: white !important;
}

.dropdown-menu {
    background-color: #23272A !important;
    border: 1px solid #989898;
    border: 1px solid rgba(0, 0, 0, 0.15);
}

.medium-green {
    background-color: #202225;
}

.light-green {
    background-color: #23272A;
}

.section-content, .activity-content, .main-content {
    background-color: #2C2F33;
}

.inner-content {
    border: 1px #333333 solid !important;
    background: #23272A;
    color: #bdbfc1 !important;
}

.inner-content p {
    font-family: Arial;
    font-size: 9pt;
    color: #dcddde;
}

em {
    font-style: italic;
    color: #bdbfc1;
}

.border-left {
    background-color: #23272A;
}

.activity-content a, .main-content a {
    color: #dcddde;
}

.hoofdstuk_icon, .opdracht_icon {
    filter: invert(100%)
}

element.style {
    background-color: rgb(35 39 42);
}

.section-content h5, .activity-content h5, .main-content h5 {
    font-size: 12pt;
    color: #ffffff !important;
    background-color: #202225 !important;
}

img[alt="Grafisch Lyceum "] {
    filter: invert(100%)
}

.form-control {
    background-color: #23272A;
    border: 2px solid #202225;
}

body {
    background-image: url(https://i.imgur.com/z69dqSK.png);
    height: 1px; /* You must set a specified height */
}
}
