css:
@-moz-document domain("albicla.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #1e2839;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #1e2839;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #3c4e71;
        }

body, main {
	background: #1e2839;
}
body, .btn:hover, .btn, .dropdown-item {
	color: #d1d5d9;
}
.btn-link {
    color: #d9ebff;
}
.btn-link:hover, a:hover {
    color: #f5faff;
}
header .input-group-navbar .btn, header .input-group-navbar .form-control, .form-control {
	background: #1e2839;
}
.post-link {
	background: #202b3e;
}
.post-link-image {
	border-color: #202b3e;
}
input#searchautocomplete, .card-header {
	background: #1e2839 !important;
}
.bg-white, #outer, .btn-a:hover {
	background: #29364E !important;
}
.nav-tabs .nav-link:focus, .nav-tabs .nav-link:hover {
    border-color: #3b434a #3b434a #475564;
}
.border-bottom, .border, .border-top {
	border-color: #475564 !important;
}
.card-body-reply {
	border-left-color: #1e2839 !important;
}
a {
    color: #e2efff;
}
.card {
    background-color: #29364E;
    border-color: rgba(255, 255, 255, .125);
}
#timeline .card-body-item:hover {
    background: #334362;
	transition: background ease-out .1s;
}
#timeline .card-body-item {
	transition: background ease-in .1s;
}
.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
    color: #d5e7f9;
    background-color: #29364E;
    border-color: #475564 #475564 #29364E;
}
textarea.form-control.border-0.p-0.shadow-none {
	background: #0000 !important;
}
.form-control {
	border-color: #3b434a;
}
.form-control {
	color: #d5e7f9;
}
.input-group-text {
    color: #d5e7f9;
    background-color: #1e2839;
    border-color: #3b434a;
}
.input-group>.custom-select:not(:first-child), .input-group>.form-control:not(:first-child) {
	background: #29364E;
}
button.hover-blue:hover, button.hover-red:hover {
	background: #29364E;
}
.text-muted {
    color: #9fa5aa!important;
}
[src='https://albicla.com/imgcache/150x150/c/data/img/no-avatar.png'], [src='/imgcache/150x150/c/data/img/no-avatar.png'], [src='/imgcache/35x35/c/data/img/no-avatar.png'], [src='https://albicla.pl/imgcache/150x150/c/data/img/no-avatar.png'] {
	filter: invert(.83);
	border-color: #d0cbb6 !important;
}
footer a, footer {
	color: #999;
}
.user-post a.mention {
    color: #70b3ff;
}
.user-post a.mention:hover {
    color: #5a92d2;
}
.dropdown-menu {
    color: #d1d5d9;
    background-color: #29364E;
    border-color: rgba(255, 255, 255, .15);
}
.dropdown-item:focus, .dropdown-item:hover {
    color: #e6e8eb;
    background-color: #334362;
}
#avatar img, #avatar-edit button {
	border-color: #29364E;
}
.btn-primary {
    color: #fff;
    background-color: #335479;
    border-color: #335479;
}
.btn-primary:not(:disabled):not(.disabled).active, .btn-primary:not(:disabled):not(.disabled):active, .show>.btn-primary.dropdown-toggle {
    color: #fff;
    background-color: #22364f;
    border-color: #22364f;
}
.btn-primary:hover {
    color: #fff;
    background-color: #2b4464;
    border-color: #2b4464;
}
.alert-success {
    color: #42ff6d;
    background-color: #398a4d;
    border-color: #398049;
}
.alert-warning {
    color: #fbff00;
    background-color: #9b8338;
    border-color: #806c32;
}
.checkbox {
	background: #0000;
}
.form-label-group input:not(:placeholder-shown), .form-control {
	background: #29364E !important;
	color: #d5e7f9 !important;
}
.post-link a:hover, .post-link a:hover p, .post-link a:hover span {
	color: #d5e7f9;
}
.form-label-group input:not(:placeholder-shown) ~ label {
	color: #aaa;
}
.select2-container--default .select2-selection--single .select2-selection__rendered {
	color: #bbb;
}
.select2-container--default .select2-selection--single {
	background: #29364E;
	border-color: #475564
}
.select2-container--default .select2-search--dropdown .select2-search__field {
	border-color: #475564;
	background: #29364E;
}
.select2-dropdown {
	background: #29364E;
	border-color: #475564;
}
.select2-container--default .select2-results__option--selected {
	background: #334362;
}
.lightbox .lb-image {
	border-color: #29364E;
}
.lb-outerContainer {
	background-color: #29364E;
}
.lb-nav a.lb-prev, .lb-nav a.lb-next, .lb-cancel {
	filter: invert(1)
}
.tt-menu {
    background-color: #29364E;
    border-color: rgba(255, 255, 255, .2);
}
.post-link-title {
	color: #fff;
}
.post-link-lead, .post-link-domain {
	color: #ccc;
}
[style="color:black"] {
	color: #fff !important;
}
.card-header {
    border-color: rgba(255, 255, 255, .125);
}
}
