    --tShade-3: #736f6da8;
    --tShade-4: #282524a8;
    --tShade-5: #82828299;
}

.img-thumbnail,
body {
    /* URL IMAGE LINE HERE:  */
    background-image: url('/*[[image]]*/');
    background-attachment: fixed;
