css:
@-moz-document url-prefix("https://beta.myshows.me") {
:root {
    --container-padding:10px;
    --sidebar-background:#0c0c0c;
    --content-background:#252425;
    --link-color: #ccc;
    --link-color-hover: #fff;
    --info-color: #fff;
    --myshows-color: #ff4920;
    --kinopoisk-color: #f60;
    --text-grey: #999;
    --text-light-grey: #eee;
    --text-dark-grey: #656565;
    --text-white: #fff;
    --dark-border: #333;
    --light-border: #eee;
    --active-item-bg: #4c4c4c;
    --darker-textarea-background: #1b1b1b;
    --list-border: #4b4b4b;
}
body {
    color: #eee;
}
.Search-results {
    box-shadow: 0px 1px 10px 0 rgb(0 0 0 / 50%);
    border: 1px solid var(--dark-border);
    background: var(--content-background);
    margin-top: 3px;
    color: var(--text-light-grey);
}
.Icon.__info {
    color: #c00;
}
.NewsItem.hero .NewsItem-title, .EpisodeItem-title, .Wasted-timeCaption  {
	color: var(--text-light-grey);
}

a.NewsDetails-metaItem, a.news-read-also-main__item, a.news-read-also-aside__item, .Sorting span  {
	color: var(--link-color);
}

.Sorting a.router-link-exact-active {
	color: var(--link-color);
    font-weight: 700;
}

.Select select {
	background: var(--darker-textarea-background);
    color: var(--text-light-grey);
    border: 1px solid var(--dark-border);
}

.Input-control, .FormField-input, input[type=checkbox] {
	background: var(--darker-textarea-background);
    color: var(--text-light-grey);
    border: 1px solid var(--dark-border);
}

.Radio-icon {
	border-color: var(--text-dark-grey);
}

.Radio>input~.Radio-icon:after {
	background: var(--text-dark-grey);
}
.Radio>input:checked~.Radio-icon:after {
    background: var(--myshows-color);
}

.ProfileNav.list .ProfileNav-item.router-link-active {
	color: var(--text-dark-grey);
}

.Search-result.highlighted {
    background: var(--active-item-bg);
}

.SearchField.light .SearchField-input {
    box-shadow: none;
    border: 1px solid var(--dark-border);
    border-right: none;
    background: var(--darker-textarea-background);
    color: var(--text-light-grey);
}

.Pagination-link {
    color: var(--text-light-grey);
	background: var(--active-item-bg);
}

.SearchAll-total span {
	color: var(--text-light-grey);
}

.link:hover, a:hover {
    color: var(--link-color-hover);
}
.Title__primary, .Title__main, .AsideHeading__primary {
    color: #fff;
}
.ListItem-slot a:hover, .EpisodeCol a:hover {
    color: var(--link-color-hover);
}
.CommentsFeed-item {
    color:var(--text-grey) !important;
}
.Note-view, .EpisodesBySeason-seasonRow {
    background-color: var(--dark-border);
}
.Row.EpisodesBySeason-seasonRow, .Row.EpisodesBySeason-episode {
    padding: 0 10px;
}
.FavoriteButton {
    color: var(--content-background);
}
.EpisodeCol.muted .EpisodeCol-title, .ShowCol-titleOriginal {
    color: var(--text-grey);
}
.ShowCol-titleOriginal {
    margin-top: 0;
    font-size: 13px;
}
.EpisodeCol.muted .EpisodeCol-title:hover {
    color: var(--link-color-hover);
}
.show-informer__show-more {
    color: var(--text-grey);
    border-bottom: 1px dashed var(--text-grey);
}
.show-informer__show-more:hover {
    color: var(--text-white);
}
.CommentsFeed {
    font-size: 13px !important;
    line-height: 19px !important;
}
.UnwatchedEpisodeItem-index {
    color: #b7b5b5;
}
.UnwatchedEpisodeItem {
    padding: 3px 3px 3px 10px;
}
.Row, .UnwatchedEpisodeItem {
    border-color: var(--list-border);
}

.Row-container {
	padding-left: 10px;
}

.Row:nth-child(2n), .UnwatchedEpisodeItem:nth-child(2n) {
    background: #303030;
}
.Page-aside:not(:empty) {
    border-color: var(--dark-border);
}
.Tabs.default .TabsItem.active, .Tabs.default .TabsItem.router-link-exact-active, .ProfileNav.list .ProfileNav-item, .ProfileNav.list .ProfileNav-logout, .WatchSoon-date {
    color: var(--text-light-grey);
}
.WatchSoon-episode, .ListItem-slot {
    color:var(--text-dark-grey);
}
.ProfileNav.list .ProfileNav-item:hover, .ProfileNav.list .ProfileNav-logout:hover {
    color: var(--text-white);
}
.User-hr {
    background: var(--dark-border);
}
.FeedItem {
    padding: 7px 5px !important;
    margin-bottom: 0 !important;
}
.FeedItem:nth-child(2n+1) {
    background: #303030;
}
.Progress-slots, .User-showsMore {
    color: var(--text-white);
}
.NewsItem-foreword {
    color: var(--text-grey);
    font-size: 13px;
    line-height: 18px;
}
.Sidebar-container, .Sidebar-item, .Tabs.default .TabsItem:not(:last-child) .TabsItem-container, .Tabs.default .Tabs-container, .Feed-group, .FeedItem:not(:last-child), .ShowDetails-section, .Episode-section.border, .Field, .AvatarUploader, .SectionAccordion {
    border-color: var(--dark-border);
}
.Community-separator {
    background: var(--dark-border);
}
.Sidebar-item.router-link-exact-active {
    background: var(--active-item-bg);
}
.Sidebar-itemText {
    color: #b7b5b5;
}
.Comment-container {
    background: var(--content-background);
    border-color: var(--dark-border);
    padding: 10px 10px;
}
.Comment.new .Comment-container {
    background: var(--dark-border);
    border-color: var(--light-border);
}
.FormTextarea {
    border: 1px solid var(--dark-border);
    background: var(--darker-textarea-background);
    color: var(--text-grey);
}
.CommentList .CommentList, .CommentList .CommentList .CommentList, .CommentList .CommentList .CommentList .CommentList, .CommentList .CommentList .CommentList .CommentList .CommentList {
    margin-left: 0;
}
.CommentList .CommentList .Comment-container {
    padding-left: 60px;
}
.CommentList .CommentList .CommentList .Comment-container {
    padding-left: 90px;
}
.CommentList .CommentList .CommentList .CommentList .Comment-container {
    padding-left: 120px;
}
.Comment {
    font-size: 13px;
}
.Sidebar-item.router-link-exact-active .Sidebar-itemText {
    color: #fff;
}
.Sidebar-item:last-of-type {
    border-bottom: none;
}
.DefaultLayout:not(.guest) .DefaultLayout-main {
    align-items: stretch;
}
.DefaultLayout-main {
    position: relative;
    z-index: 3;
    display: flex;
    flex-direction: row;
    flex-grow: 1;
    align-items: stretch;
    word-wrap: break-word;
}
.Sidebar-item:not(.router-link-exact-active) .Sidebar-itemIcon {
    filter: grayscale(1);
}
.ShowStatusBar-option {
    padding: 7px 11px 7px;
    background: #d32f2f;
    color: var(--text-white);
}
}
