    }
    .gk__pt__inr {
        background-color: #151515;
    }
    .gk__pt.has--tbs .gk__pt__inr {
        border: 1px solid #edaf00;
    }
    .gk__pt__lst__itm + .gk__pt__lst__itm:before {
