css:
/* i really want this to be global */

* {
   font-family: 'Merriweather', serif!important; 
}

body.tp-modal-open {
  overflow: auto !important;
}

body {
  background-color: #ffffff;
}

.article-inner-content {
  box-shadow: none;
}

.site {
  border: none;
}

.thelocal-main-content-left {
  width: auto;
}

.post-thumbnail, .entry-the-post {
  margin: 1em;
}

.header-branding {
  max-width: 900px;
}

.thelocal-main-content-short {
  max-width: 900px;
}

.entry-title {
  margin-left: 0;
  margin: 1em;
}

.post-thumbnail {
  margin: 1em 0;
}

img:not(img.custom-logo) {
  border-radius: 4px;
  background-color: #dddddd;
  background-image: url("data:image/svg+xml;base64,");
  background-size: 40px;
  background-position: center center;
  background-repeat: no-repeat;
}

.post-thumbnail-credit {
  margin: 1em;
}

.qc-cmp-cleanslate, 
.main-navigation, 
.login-box, 
.menu-header-visible-main, 
.section-sponsored, 
div[class*="promo"], 
footer,
.ad_container, 
.tp-modal, 
.donate-message,
hr,
.tp-backdrop, 
.entry-meta,
.advertisement,
.section-title,
.thelocal-main-content-right,
.entry-type-title-members,
.article-content-member-type,
.comments-area, 
.article-conent-origin-country-bottom,
.entry-meta-share-bottom {
  display: none!important;
}

.article-block-50-more-news {
  border-left: none!important;
}

.custom-logo {
  width: 120px;
  height: auto;
}
.custom-logo-link {
  margin: 0!important;
  filter: saturate(0) brightness(0.7) contrast(3);
}

.bg-orange {
  background-color: black!important;
}

.article-block {
  box-shadow: none;
  margin-top: 2em;
}

.article-list-container {
  margin-bottom: 2em;
}
  

.entry-the-post { 
  line-height: 180%;
  padding: 1em 0;
  font-size: 20px;
}

.entry-the-post a { 
  color: inherit;
  text-decoration: underline;
}
