css:
@-moz-document url-prefix("http://gaugan.org/gaugan2/") {

	td[width="8%"][align="center"] h2 {
		height: 22px;
		font-size: 0px!important;
		background: url(data:image/png;base64,) center/100% no-repeat;
		left: -10%;
		position: relative
	}

	html,
	body {
		background: #313152;
		width: 100%!important;
		height: 100%!important
	}
	[value*="tutorial"] {
		position: absolute;
		font-size: 10px!important;
		background: #24272e;
		box-shadow: 0 0 0 2px ;
		color: #7a8b97;
		top: 7.5%;
		padding: 4px;
		border-radius: 5px;
		border: 0
	}
	canvas#viewport {
		position: inherit;
		top: 26%;
		left: 0px;
		transform: scale(62%);
		transform-origin: 40% 0%;
	}
	canvas#output {
		position: inherit;
		top: 56%!important;
		left: 33%!important;
		transform: scale(62%);
		transform-origin: -50% 0%;
		background: #7a8b97
	}
	[style="touch-action: none; cursor: sw-resize;"] {
		cursor: cell!important
	}
	[width="4%"] button {
		transform: scale(0.4);
		margin: -16px
	}
	[id*="example"] {
		margin: -15px !important;
		transform: scale(0.4);
		left: 0!important
	}
	[width="4%"] button:hover,
	[id*="example"]:hover {
		transform: scale(0.6);
		transition: 0.1s
	}
	.random,
	td[width="4%"] button {
		filter: invert(0.7)
	}
	.random {
		position: inherit;
		transform: scale(0.6);
		transform: rotate(0deg);
	}
	.random:not(:active) {
		position: inherit;
		transform: scale(0.6);
		transform: rotate(6480deg);
		transition: 7s
	}
	[width="10%"] {
    width: auto;
	}
	canvas#brush_color {
		position: relative;
		background-color: #7a8b97!important;
		border-color: transparent;
		border: 0px;
		margin: 0px;
		box-shadow: 0 0 0 3px #7a8b97
	}
	[width="10%"][valign="center"] {
		position: relative;
		width:8%;
		padding-left: 12px!important
	}
	[width="8%"][valign="center"] {
		position: relative;
		width:6%;
	}
	[width="2%"][align="center"] {
		position: relative;
		left: -10%
	}
	.lbl-river:hover,
	button:hover {
		color: #fff!important
	}
	.table {
		border: 0px solid black;
		border-spacing: 0;
		width: 100%
	}
	table.table {
		background: #26262c;
		border-bottom: 2px solid #1c1c1c!important;
	}
	#main_frame_div table.table {
		position: inherit;
		margin-bottom: -2.5%!important;
	}
	#main_frame_div {
		margin-bottom: -313px
	}
	.vertical_line {
		border-left: 0px solid black;
		height: 2%;
		position: absolute;
		left: 50%;
	}
	[style*="color"] {
		color: #fff!important
	}
	h1,
	h2,
	h3,
	h4,
	h5,
	h6,
	button,
	[class*="btn"] {
		color: #7a8b97;
		font-family: "Segoe UI", Arial, sans-serif;
		font-weight: 400;
		font-size: small!important;
	}
	.brush_circle_active {
		border-radius: 90px
	}

	h3 {
		font-size: 14px;
	}
	tr td[width="8%"][valign="top"] {
		background: #16161f;
		border-radius: 0 10px 10px 0;
		vertical-align: top;
		margin-top: 8px!important;
		width: auto;
		display: inline-block;
		position: fixed;
		padding: 10px;
		z-index: 9000
	}
	.w3-red {
		color: #fff!important;
		background-color: #d3561b!important;
		margin-top: -460px;
		font-size: 11px;
		width: 150px;
		border-radius: 0 10px 10px 0;
		position: fixed;
		padding: 5px;
	}
	.w3-red:hover {
		z-index: 10000!important;
	}
	.w3-grey,
	.w3-hover-grey:hover,
	.w3-gray,
	.w3-hover-gray:hover {
		color: #fff!important;
		background-color: #535353!important;
	}
	.w3-button:hover {
		color: #d5d5d5!important;
		background-color: #464646!important;
	}
	.w3-light-grey,
	.w3-hover-light-grey:hover,
	.w3-light-gray,
	.w3-hover-light-gray:hover {
		color: #7a8b97!important;
		background-color: #4f4f4f!important;
	}
	[style="opacity: 0.25; transition: opacity 4s ease 0s;"] {
		background-color: #fff;
		border-radius: 22px;
		transform: scale(0.4)!important;
		filter: invert(70%) sepia(32%) saturate(1924%) hue-rotate(1040deg) brightness(96%) contrast(95%) drop-shadow(0 2px 7px)!important;
		opacity: 1!important;
		transition: 4s!important;
	}

	input[type='checkbox']:checked:before {
		content: '✔️';
		color: #fff;
		font-size: 10px;
		border-radius: 50%;
	}
	input[type='checkbox'] {
		width: 14px !important;
		height: 14px !important;
		-webkit-appearance: none;
		-moz-appearance: none;
		-o-appearance: none;
		appearance: none;
		box-shadow: 0 0 0 2px #d3561b;
		font-size: 0.8em;
		background: #fff;
		border-radius: 22px
	}
	.save_render:after {
		content: "Save_Image"!important;
		font-size: 20px!important;
		font-weight: 800!important;
		position: inherit;
		margin-left: 100%;
		border-radius: 0 9px 9px 0;
		box-shadow: 0 0 0 2px;
		padding: 8px;
		background: #d3561b;
		color: #000;
		text-decoration: underline;
	}
	#save:after {
		content: "Save_ZIP"!important;
		font-size: 20px!important;
		font-weight: 800!important;
		position: relative;
		margin-left: -180.8%;
		border-radius: 0 9px 9px 0;
		box-shadow: 0 0 0 2px;
		padding: 8px;
		z-index: 90000!important;
		overflow: visible;
		bottom: 338px!important;
		background: #d3561b;
		text-decoration: underline;
		color: #fff
	}
	.save {
		display: block;
		height: 48px;
		width: 48px;
		margin-top: -34%;
		margin-left: 15%;
		background-size: 100%;
		filter: invert(1);
		position: relative
	}
	.save_render {
		display: flex;
		height: 48px;
		width: 48px;
		margin-top: -34%;
		margin-left: -21%;
		background-size: 100%;
		filter: invert(1);
	}


	/*2022*/
	/*Userstyle Created for Gaugan2 by YAD*/
}
