namespace url(http://www.w3.org/1999/xhtml);



/* Aendert das Kwick-Aussehen. Von http://www.kwick.de/KiBRiT */



/* Original von http://www.kwick.de/Pitchshifter (wenigstens diese Zeile bitte stehen lassen...) */



@-moz-document domain("www.kwick.de") {







body {background:#C4CEDF none repeat scroll 0 0 !important; color:#717 !important }







#wrapper #header {background:transparent url(http://img179.imageshack.us/img179/269/200708091454391kd8.jpg) repeat scroll left center !important}







#wrapper #topline {height:0px !important}







#wrapper {background:#FFF none repeat scroll center center !important; border: 6px solid #FFF !important; width:962px !important}







.sidebarNick {width: 100px !important}







.module {background:#000000 none repeat scroll 0 0 !important;}







table {background:#FFF none repeat scroll 0 0 !important}







#t2uo {background:#CCC !important; border-bottom:10px solid #CCC !important; border-left:10px solid #CCC !important}







a:link {color:#444474 !important;}







a:visited {color:#444474 !important}







a:hover {color:#444454 !important; text-decoration:underline !important}







#sidebar .box {background:#F7F7F7 !important; border-right:1px solid #CCCCCC !important; border-left:1px solid #CCCCCC !important; border-bottom:1px solid #F7F7F7 !important}







#sidebar .box-bottom {height:7px !important;border-bottom:0px solid #CCCCCC !important; border-left:1px solid #CCCCFF !important}







#sidebar #buddys td.bItem {background:#F7F7F7 none repeat scroll 0 0 !important; border-bottom:1px solid #CCCCFF !important;}







#sidebar #ticker {background:#F7F7F7 !important}







#sidebar .tbInfobox {border-right:1px solid #CCCCCC !important;}







#sidebar .box-header, #ticker .box-header {background:#F7F7F7 !important; border-bottom:2px dotted #ADADAD !important; border-left:1px solid #CCC !important; border-top:1px solid #CCCCCC !important; border-right:1px solid #CCCCCC !important; width:173px !important}







#sidebar .box-top {background:#F7F7F7 none right top no-repeat !important; border-right:1px solid #CCCCCC !important; border-left:1px solid #CCCCCC !important; border-top:1px solid #CCCCCC !important}







#sidebar table {background:#F7F7F7 !important}







.module .header {background:#E5EDF5 none no-repeat right top !important; border-top:1px solid #C4CEDF !important; border-left:1px solid #C4CEDF !important; border-right:1px solid #C4CEDF !important; border-bottom:1px solid #C4CEDF !important}







.module .header h4 {background:#E5EDF5 none no-repeat left top !important;}







.module .content {background:#F2F4F7 none repeat left top !important; border-bottom: 1px solid #C4CEDF !important; border-left: 1px solid #C4CEDF !important; border-right: 1px solid #C4CEDF !important}







.modCom .header {background:#E5EDF5 none no-repeat right top !important; border-bottom: 1px solid #C4CEDF !important; border-top: 1px solid #C4CEDF !important; border-right: 1px solid #C4CEDF !important}







.modCom .header h4 {background:#E5EDF5 none no-repeat left top !important; color:#3B5998 !important; border-bottom: 1px solid #C4CEDF !important; border-left: 1px solid #C4CEDF !important;}







.modCom .content {background:#F2F4F7 none repeat scroll 0 0 !important; border-bottom: 1px solid #C4CEDF !important}







.nolink {color:#6A7171 !important;}







.tabnav {background:none repeat-x bottom #B0B6BF !important;}







.tabnav li.act {background: none #5C75AA !important}







.tabnav li.act a, .tabnav li.act a:hover {color:#FFF !important;background: none repeat-x bottom #5C75AA !important} 







table.list .alt1, .rowlist.a {background-color:#F7F7F7 !important}







table.list .alt2, .rowlist.b {background-color:#F7F7F7 !important}







#lay2col-r .colcontent {background:#F7F7F7 none no-repeat bottom !important; border-top:1px solid #C4CEDF !important; border-left:1px solid #C4CEDF !important; border-right:1px solid #C4CEDF !important; border-bottom:1px solid #C4CEDF !important}







.lay2col-r .colcontent {background:#F7F7F7 none no-repeat bottom !important; border-top:1px solid #C4CEDF !important; border-left:1px solid #C4CEDF !important; border-right:1px solid #C4CEDF !important; border-bottom:1px solid #C4CEDF !important}







.colcontent-h {height:0px !important}







.mtable td {background:#ECEFF5 !important; border-right: 1px solid #C4CEDF !important}







.mtable-right {background:#ECEFF5 none right top no-repeat !important; border-bottom: 1px solid #C4CEDF !important; border-right: 1px solid #C4CEDF !important}







.mtable-left {background:#ECEFF5 none right top no-repeat !important; border-bottom: 1px solid #C4CEDF !important; border-left: 1px solid #C4CEDF !important; border-right: 0px solid #C4CEDF !important}







.mtable th {background:#F7F7F7 !important; border-top: 1px solid #C4CEDF !important;}







.mtable .mtc {background:#F7F7F7 !important; border-bottom: 0px solid #C4CEDF !important; border-right: 0px solid #C4CEDF !important; border-top: 1px solid #C4CEDF !important; border-left: 0px solid #C4CEDF !important}







.mtable .lb {border-left: 1px solid #C4CEDF !important}







.mtable tr.delimiter td {border-color: #C4CEDF -moz-use-text-color !important; background: #FFF !important}







.Paginator a, .Paginator a:visited, .Paginator .this-page { background: transparent !important; color:#000 !important}







div.puser {background:transparent none center !important}







h1,h2,h3 {color:#D15A1F !important}







.jDrop table {background-color:#000000 !important}







.modCom .content-footer,.module .content-footer {height:0px !important}







.bc,#header #navigation #meta {visibility:hidden !important; line-height:0px !important}







#footer {line-height:22px !important; height:3px !important; padding:0px 0 0 12px !important}







#topnav1 li {width:87px !important}







#topnav1 span {display:inline !important}







#topnav2 ul {background:#B0B6BF !important}







#topnav2 {padding: 0 0 0 0px !important; background:#254588 !important}







#topnav1 {float:left !important;}







#topnav1 a#startseite,#topnav1 a#mitglieder, #topnav1 a#clans, #topnav1 a#events, #topnav1 a#forum, #topnav1 a#chat, #topnav1 a#magazin, #topnav1 a#community {background: #3B5998 none no-repeat scroll 0 0px !important; color:white !important; padding-top:5px !important; width:87px !important; text-align: center !important}







#topnav1 a#startseite-act, #topnav1 a#mitglieder-act, #topnav1 a#clans-act, #topnav1 a#forum-act, #topnav1 a#chat-act, #topnav1 a#events-act, #topnav1 a#magazin-act, #topnav1 a#community-act 



{color:#FFFFFF !important; background: #5C75AA none no-repeat scroll 0 0px !important; font-weight:bold !important; padding-top:5px !important; width: 87px !important; text-align: center !important}







#topnav2 ul li.act a, #topnav2 ul li.act a:hover {background:#5C75AA none bottom repeat-x !important; color:#FFF !important}







#topnav2 ul li.act {background:#5C75AA none bottom repeat-x !important}







.jDrop, .jDrop td.r{background-color:#E5EDF5 !important; border-bottom:0px solid #5C75AA !important; border-top:0px solid #5C75AA !important}







.jDrop td.l {background-color:#5C75AA !important; border-bottom:0px solid #E5EDF5 !important; border-top:0px solid #E5EDF5 !important}







.jDrop tr.seperator td.r, .jDrop tr.seperator td {background:transparent none repeat-x center !important; height:2px !important; background-color:transparent !important}







html>body>div>div>table>tbody>tr>td>div>div>div>table>tbody>tr>td>table>tbody>tr>td>img:first-child {visibility:hidden !important}



}
