css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("msdn2.microsoft.com") {
  div#mainBody>p {padding-left:14px;color:#000;font-size:120%; font-family:tahoma,arial,helvetica;}
  div#mainBody>p>b {font-size:110%; width:100px; display:block; color:#333;padding-top:3px;}
  div#mainBody>p>span {font-weight:bold;}
  div.majorTitle {border-width:1px 1px 0 1px; border-style:solid;border-color:#ccc; background-color:#fff;margin:0;padding:3px 3px 0 3px;}
  div.title {border-width:0 1px 1px 1px; border-style:solid;border-color:#ccc; background-color:#fff;margin:0 0 8px 0;padding:0 3px 3px 3px;font-size:200%;font-weight:bold;}
  div.contentPanel{background-color:#f5f5f5;}  
  div.VsBar {width:99%;border:1px solid #f5f5f5;margin-top:6px;}
  div.MTPS_CollapsibleRegion {border-width:1px; border-color:#eaeaea; border-style:solid;background-color:#fff;padding:8px !important;}
  a.CollapseRegionLink,a.CollapseRegionLink:link,a.CollapseRegionLink:visited{color:#336699 !important;font-size:120% !important;}
  a.CollapseRegionLink:hover{color:#990000 !important;font-size:120% !important;}
  img.CollapseRegionImg{padding:0 3px 10px 0 !important;margin:0 !important;}
  div.code{background-color:#ffffff !important;border:1px solid #ccc !important;width:99%;margin-bottom:10px !important;}
  div.CodeSnippetTitleBar{background-color:#efefef !important;}
  pre.code{background-color:white !important;}
  pre.code span{font-weight:bold;}
  #mainSection table th{background:#efefef none repeat scroll 0%;}
  #mainSection div.alert table th{border:1px solid #990000; color:#990000; font-size:120%;}
  #mainSection div.alert table th img{margin:0;padding:9px 5px 0 0;}
  #mainSection div.alert table td{border:1px solid #990000;}
}
