css:
@-moz-document domain(edufpmi.bsu.by) {
.block_myoverview, .block_recentlyaccessedcourses{
    border: none;
    box-shadow: none;

  }
  .card-body{
    padding: 0;
  }
  .dashboard-card{
   max-width: 20rem;
   border-radius: 10px;
   height: 25rem;
  }
.dashboard-card:last-child{
    flex-grow: 1;
    flex-basis: 50%;
  }
  .dashboard-card-img{
     border-top-left-radius: 10px;
     border-top-right-radius: 10px;
     min-height: 10rem;
  }
  .course-info-container, .dashboard-card-footer{
   border-bottom-left-radius: 10px;
   border-bottom-right-radius: 10px;
  }
  .dashboard-card-deck{
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    align-items: center;
  }

#page-header .card, #region-main .card{
    border-radius: 10px;
}

#page-header .card .page-context-header{
    display: flex;
    align-items: center;
    flex-wrap: wrap;
}
#page-header .card>.card-body{
    position: relative;
}
#page-header .card>.card-body form{
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    right: 2%;
}
#page-header .card>.card-body #message-user-button{
    padding: 1px 8px;
    border-radius: 5px;
}
#page-header .card>.card-body #message-user-button i{
   font-size: 25px;
}

#page-header .card>.card-body #message-user-button .header-button-title{
   display: none;
}


#page-header .card>.card-body form button{
    font-size: 0;
    padding: 1px 8px;
    border-radius: 5px;
}
#page-header .card>.card-body form button:after{
    font-size: 25px;
    content: '\2699';
}


#page-header .card .page-header-image{
    margin-bottom: 0;
}
#region-main>.card>.card-body{
   padding: 0;
}

.block_recentlyaccessedcourses{
    margin-bottom: 0 !important;
}
.block_recentlyaccessedcourses .dashboard-card{
   max-width: 20rem;
   border-radius: 10px;
   height: 15rem;
  }



}
