css:
@-moz-document domain("www.tagesschau.de") {
.header__items {
    height: 5rem;
    -moz-transform: scale(0.7);
}
.layout-content {
    padding-top: 10rem;
}
.teaser__topline,
.list-element__topline {
    margin-bottom: 0.8rem !important;
    font-size: 1.5rem !important;
    line-height: 1.6rem !important;
    font-style: italic;
    padding-left: 1rem;
}
.teaser__headline,
.list-element__headline {
    font-size: 2rem !important;
    line-height: 2.1rem !important;
    margin-bottom: 0.8rem !important;
}
.teaser__shorttext,
.list-element__shorttext {
    font-size: 1.5rem !important;
    line-height: 1.8rem !important;
    width: 95% !important;
    padding-bottom: 0.5rem;
}
.teaser {
    padding-bottom: 1rem !important;
}
div.teaser > div:nth-child(1),
li.list-element > div:nth-child(1),
a.list-element__link > div:nth-child(1),
div.teaser > a > div:nth-child(1) {
    width: 20% !important
}
div.teaser > div:nth-child(2),
li.list-element > div:nth-child(2),
a.list-element__link > div:nth-child(2),
div.teaser > a > div:nth-child(2) {
    width: 80% !important
}
.teaser__teaserinfo,
.list-element__teaserinfo {
    padding: 0rem 1rem 0 !important;
}
.teaser .mubu-container {
    display: none !important;
}
.teasergroup.color--tongue {
    margin-bottom: 2rem;
}
mg.js-image.fade-in {
    opacity: 1;
    transition: opacity .1s;
}
.teasergroup {
    margin-top: 0rem !important;
}
span.label {
    font-size: 1rem !important;
}
}
