css:
 
 /* changes to 3x3links.com: BG=Gray, title-text smaller. 7-24-09 :: tonyd :: 
    edits to text make in h1. But go for it :)*/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.3x3links.com") {

    html, body {
    margin: 0 !important;
    padding: 0 !important;24-09
    }
    body {
    background: #f6f5f1 !important;     /* <-edit backround-color here, was #ffff */ 
    }
    h1 {
    font: bold small-caps 1.25em Georgia, "Times New Roman", Times, serif !important;
    text-transform: uppercase !important;
    color: #B05A04 !important;
    }
    h2 {
    font: small-caps 1em Georgia, "Times New Roman", Times, serif !important;
    color: #B06E04 !important;
    }
    p, a {
    font: 0.9em Arial, Geneva, Helvetica, sans-serif !important;
    color: #999 !important;
    }
    #container {
    width: 780px !important;
    margin: 0.5em auto 0.5em auto !important;
    background: #fff !important;
    text-align:center !important;
    -moz-border-radius: 20px !important;
    padding: 0.5em !important;
    }
    #container li {
    float: left !important;
    width: 150px !important;
    height:150px !important;
    list-style-type:none !important;
    border: solid #bbb !important;
    border-width: 1px 1px 0 0 !important;
    -moz-border-radius-topright:10px !important;
    margin:1em !important;
    text-align:center !important;
    display:inline !important;
    }
    li img { border: none !important; }
    ul#photos li a span { display: none !important; }
    ul#photos li a:hover span {
    display: block !important;
    color: #333 !important;
    background-color:tan !important;
    -moz-border-radius: 10px !important;
    padding: .25em 0 .25em 0 !important;
    text-transform: uppercase !important;
    font-size: .85em !important;
    }
    #photo, #pictureScreenObject {
    display:none !important;
    position: absolute !important;
    left:15% !important;
    z-index:100 !important;
    background:#fff !important;
    padding:2em !important;
    margin:1em !important;
    font: 0.9em Arial, Helvetica, sans-serif !important;
    border:1px solid #bbb !important;
    text-align: center !important;
    -moz-border-radius: 20px !important;
    } 


}
