css:
@-moz-document domain("moodle.technikum-wien.at") {
#page {
    background-color: #202020;
}
#page-wrapper {
    background-color: #00689e;
}
.message-app .body-container {
    background-color: #161616;
    color: #FFFFFF;
}
.h-100 {
    background-color: #252525;
    color: #FFFFFF;
}
.FHTWtable td, #FHTWtable td, .FHTWtable th, #FHTWtable th {
    background-color: #252525;
    color: #FFFFFF;
}
.pagelayout-frontpage #region-main .theme_fhtw-resources {
    background-color: #161616;
    color: #FFFFFF;
}
.FHTWtable {
    background-color: #202020;
    color: #FFFFFF;
}
.texrender {
    background-color: #FFF;
    border: 2px solid #FFF;
}
.preferences-container .preference-table tr td:nth-child(even) {
    background-color: #080808;
}
.preferences-container .preference-table tr th:nth-child(even) {
    background-color: #080808;
}
.message-app {
    background-color: #101010;
    color: #FFFFFF;
}
.message-app .day {
    color: #FFFFFF;
}
.message-app a, .message-app .btn-link {
    background-color: #101010;
    color: #FFFFFF;
}
.message-app .btn-link:hover, .message-app .btn-link:focus {
    background-color: #252525;
    color: #FFFFFF;
}
.message {
    background-color: #303030!important;
    color: #FFFFFF!important;
}
.jumbotron {
    background-color: #202020;
    color: #FFFFFF;
}
.table {
    color: #FFFFFF;
}
.generaltable tbody tr:hover {
    color: #00689e;
}
.path-calendar .maincalendar .calendar_event_course {
    background-color: #702020;
    color: #FFFFFF;
}
.calendar_event_course {
    background-color: #002540;
    color: #FFFFFF;
}
.block .minicalendar td a {
    color: #70FF70;
}
.card-footer {
    background-color: #202020!important;
    color: #FFFFFF;
}
.singleselect {
    background-color: #161616;
    color: #FFFFFF;
}
.urlselect {
    background-color: #161616;
    color: #FFFFFF;
}
.bg-white {
    background-color: #090909!important;
}
.forumpost.unread .row.header, .path-course-view .unread, span.unread {
    background-color: #090909;
}
.scfhtw .topiclist {
    background-color: #181818;
}
.block_fhtwoverview .nav-tabs .nav-link {
    border-bottom: 1px solid #00689e;
    background-color: #181818;
    border-color: #090909;
}
.block_fhtwoverview .nav-tabs .nav-link.active, .block_fhtwoverview .nav-tabs .nav-item.show .nav-link {
    border-bottom: 1px solid #00689e;
    background-color: #181818;
    border-color: #090909;
}
.scfhtw .semesterheading {
    background-color: #00689e;
    color: #FFFFFF;
}
.scfhtw .generalsection .sectionname {
    background-color: #00689e;
    color: #FFFFFF;
}
.scfhtw .sectionbox.generalsection {
    border-left: 7px solid #00689e;
    color: #FFFFFF;
}
.scfhtw .presence .sectionname {
    background-color: #006600;
    border-color: #101010;
    color: #FFFFFF;
}
.scfhtw .selfstudy .sectionname {
    border-color: #101010;
}
.scfhtw .sectionblockboxname {
    background-color: #404040;
}
.scfhtw .sectionblockboxname h3 {
    color: #FFFFFF;
}
.scfhtw .sectionblockbox {
    border-left: 7px solid #404040;
}
.scfhtw .sectionbox.presence {
    border-left: 7px solid #006600;
    color: #FFFFFF;
}
.card-body {
    background-color: #101010;
    color: #FFFFFF;
}
#region-main {
    background-color: #080808;
    color: #FFFFFF;
    border: 1px solid #555555;
}
.generaltable {
    color: #FFF;
}
.list-group {
    background-color: #080808;
    color: #FFFFFF;
    border: 1px solid #555555;
}
.modal-content {
    background-color: #121212;
    color: #FFFFFF;
}
.course-content {
    background-color: #101010;
    color: #FFFFFF;
    border: 1px solid #080808;
}
.scfhtw .courseinfobox {
    background-color: #181818;
    color: #FFFFFF;
}
.infosheader {
    background-color: #222222;
    color: #FFFFFF;
}
#message-user-button {
    color: #FFFFFF;
}
.teachername {
    color: #FFFFFF;
    border-bottom: 2px solid #151515;
}
.list-group-item {
    background-color: #101010;
    color: #FFF;
}
.list-group-item-action:hover, .list-group-item-action:focus {
    background-color: #303030;
    color: #FFF;
}
.infoitem {
    color: #FFFFFF;
}
.scfhtw .courseinfobox a {
    color: #FFFFFF;
}
.path-mod-assign td.submissionstatussubmitted, .path-mod-assign div.submissionstatussubmitted, .path-mod-assign a:link.submissionstatussubmitted {
    background-color: #005808;
    color: #FFF;
}
.path-mod-assign td.submissiongraded, .path-mod-assign div.submissiongraded {
    background-color: #005808;
    color: #FFF;
}
.format-tiles .course-content ul.tiles .tile {
    box-shadow: 2px 4px 5px 0 #000;
}
.breadcrumb-item {
    color: #FFF;
}
.breadcrumb-item a {
    color: #00689e;
}
/* Minitests */
 .que.ddwtos .group1 {
    background-color: #101010;
}
.que .info {
    background-color: #202020;
    color: #FFF;
    border-color: #303030;
}
.que .formulation {
    background-color: #202020;
    color: #FFF;
}
.custom-select:disabled {
    background-color: #101010;
    color: #FFF;
}
.atto_image_button_text-top.img-responsive, .atto_image_button_middle.img-responsive, .atto_image_button_text-bottom.img-responsive {
    background-color: #FFF;
}
.que.essay .qtype_essay_response.readonly {
    background-color: #101010;
    color: #FFF;
    border: 1px solid #303030;
}
.que.ddimageortext div.droparea .draghome {
    background-color: #101010;
    color: #FFF;
}
table.quizreviewsummary th.cell {
    background-color: #101010;
    color: #FFF;
}
table.quizreviewsummary td.cell {
    background-color: #101010;
    color: #FFF;
}
.que.ddimageortext .draghomes .draghome {
    background-color: #101010;
    color: #FFF;
}
.generaltable th, .generaltable td {
    background-color: #101010;
    color: #FFF;
    border: 1px solid #303030;
}
.que.gapfill span.draggable.answers {
    background-color: #101010;
    color: #FFF;
}
}
