css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hobowars.com") {
	/* 
	** Hobowars colour scheme changer 
	** By That's Numberwang! (OT)
	** This is SPARTAAAAA!!!!!
	*/
	* { 	}
	body {margin: 0 0 0 0;}
	textarea, form, input, body {
		color:#ccc !important;
		background:#222 !important;
	}
	TR {
		color:#999 !important;
		background:#444 !important;
	}

	TD[bgcolor="#d7d7d7"], TD[bgcolor="#dedede"], 
	td[bgcolor="#f5f5f5"], td[bgcolor="#e6e6e6"],
	td[bgcolor="#e0e0e0"] {
		color:#ccc!important;
		background:#555!important;
	}
	TD[bgcolor="#eeeeee"], TD[bgcolor="#efefef"],
	TD[bgcolor="#f3f3f3"], TD[bgcolor="#f2f2f2"] {
		background: #666 !important;
		color:#bbb !important;
	}
	TD[bgcolor="#f8f8f8"], TD[bgcolor="#cccccc"] {
		background: #444 !important;
		color:#bbb !important;
	}
	
	TD [background="http://www.hobowars.com/images/rounded0.gif"], 
	TD [background="http://www.hobowars.com/images/rounded1.gif"], 
	TD [background="http://www.hobowars.com/images/rounded2.gif"] {
		background: #555 !important;
	}

	TD [background="http://www.hobowars.com/images/percent03.gif"] {
		background: #666 !important;
	}
	TD [background="http://www.hobowars.com/images/percent02.gif"] {
		background: #999 !important;
	}

	td[width="2"]{
		display:none !important;
	}
	
	font[color="#ff1100"] {color:#c76 !important;}
	font[color="#22a100"] {color: #8b6 !important;}
	font[color="red"] {color: #d76 !important;}
	font[color="green"] {color: #8d6 !important;}
	font[color="#ff0000"] {color: #d76 !important;}
	font[color="#0000ff"] {color: #67d !important;}
	font[color="#006600"] {color: #4c5 !important;}
	font[color="#9900cc"] {color: #c9c !important;}
	font[color="#003300"] {color: #6c0 !important;}

	FONT[color="#ffffff"] {color:#ccc !important;}
	A:link {COLOR: #c90 !important;}
	A:visited {COLOR: #c90 !important;}
	A:hover {COLOR: #0cc !important;}
	TABLE {
		color:#ccc !important;
		background-color:#222 !important;
	}
	TEXTAREA {
		color:#ccc !important;
		background-color:#222 !important; 
	}
	.menuList {
		color:#bbb !important;
		background-color:#555 !important; 
	}
	.menuList a:link {
		color: #bbb !important;
	}
	.menuList a:visited {
		color: #bbb !important;
	}
	.menuList a:hover {
		background: #666 !important;
		color: #ddd !important;
	}
	h4 {
		color:#bbb !important;
		background-color:#555 !important; 
	}
	td[height="90"] {
		height:0% !important; 
	}
	table[height="70"] {
		height:0% !important; 
	}
	
	img[src="http://www.hobowars.com/images/become_donator.gif"] {
		margin-bottom:0px !important; 
	}

}
