/* ==UserStyle==
@name           Messenger BetterTTV/FrankerfaceZ emotes
@namespace      github.com/openstyles/stylus
@version        1.0.2
@description    Replaces some default emotes with the bttv/ffz/twitch emotes.
@author         tysian
==/UserStyle== */
@-moz-document domain("www.messenger.com"),
domain("www.facebook.com"),
regexp("^https?:\\/\\/www.facebook.com\\/messages\\/t\\/.*") {
	/* Set variables */
	:root {
		/* Set image size */
		--image-size: 24px;
	}

	/* Emoji picker remove bg */
	._4f4q {
		background: none;
		border-radius: 0;
	}

	/* Remove fixed emoji width */
	.bsnbvmp4,
	.k7cz35w2,
	:not(.ecm0bbzt)>.hhz5lgdu {
		width: auto;
		height: auto;
	}

	div[role="listbox"] span img {
		height: 100%;
		width: auto;
		margin: 0 3px;
	}

	/* smileW */
	img[alt=":)"],
	img[alt="🙂"] {
		content: url(https://cdn.frankerfacez.com/emoticon/179088/4);
		width: auto;
		min-height: var(--image-size);
	}

	/* Sadge */
	img[alt=":("],
	img[alt="☹"],
	img[alt="😞"],
	img[alt="🙁"] {
		content: url(https://cdn.frankerfacez.com/emoticon/425196/4);
		width: auto;
		min-height: var(--image-size);
	}

	/* BibleThump */
	img[alt=":'("],
	img[alt="😢"],
	img[alt="😭"] {
		content: url(https://i.ya-webdesign.com/images/bible-thump-png-6.png);
		object-fit: contain;
		width: auto;
		min-height: var(--image-size);
	}

	/* rageW */
	img[alt="😡"],
	img[alt="😠"],
	img[alt=">:("] {
		content: url(https://cdn.frankerfacez.com/emoticon/243028/4);
		width: auto;
		min-height: var(--image-size);
	}

	/* PepoG */
	img[alt="🤔"] {
		content: url(https://cdn.frankerfacez.com/emoticon/234378/4);
		width: auto;
		min-height: var(--image-size);
	}

	/* KEKW */
	img[alt="😆"],
	img[alt="😂"],
	img[alt="🤣"] {
		content: url(https://cdn.frankerfacez.com/emoticon/381875/4);
		width: auto;
		min-height: var(--image-size);
	}

	/* BloodTrail */
	img[alt="(y)"],
	img[alt="👍"],
	img[alt="󰀀"] {
		content: url(https://www.streamscheme.com/wp-content/uploads/2020/04/bloodtrail-emote.png);
		width: auto;
		min-height: var(--image-size);
	}

	/* D: */
	img[alt=":O"],
	img[alt=":o"],
	img[alt="😲"],
	img[alt="😯"],
	img[alt="😮"],
	img[alt="😦"],
	img[alt="😧"] {
		content: url(https://cdn.betterttv.net/emote/55028cd2135896936880fdd7/3x);
		width: auto;
		min-height: var(--image-size);
	}

	/* shrug */
	img[alt^="🤷"] {
		content: url(https://cdn.frankerfacez.com/emoticon/267606/4);
		width: auto;
		min-height: var(--image-size);
	}

	/* peepoThink */
	img[alt^="💁"] {
		content: url('https://cdn.frankerfacez.com/emoticon/234378/4');
		min-height: var(--image-size);
		width: auto;
	}

	/* Prayge */
	img[alt="🙏"] {
		content: url('https://cdn.frankerfacez.com/emoticon/507766/4');
		width: auto;
		min-height: var(--image-size);
		object-fit: contain;
	}

	/* PepePains */
	img[alt="😫"] {
		content: url('https://cdn.frankerfacez.com/emoticon/227992/4');
		width: auto;
		min-height: var(--image-size);
	}

	/* widePeepoHappy */
	img[alt=":3"] {
		content: url('https://cdn.frankerfacez.com/emoticon/270930/4');
		width: auto;
		min-height: var(--image-size);
	}
}
