css:
@-moz-document domain("youtube.com") {

ytd-button-renderer.style-scope.size-small.style-suggestive > a {
    padding: 0px 10px;
    height: 28px;
    font-size: 11px;
}
.ytp-large-width-mode .ytp-error .ytp-error-content .ytp-error-icon-container {
    height: 200px;
    width: 340px;
    background-image: url(https://steamuserimages-a.akamaihd.net/ugc/250338860238740863/3E6CDF0C040D0008A64A986D3127061321FF2BC0/);
}
yt-icon.yt-player-error-message-renderer {
    --iron-icon-fill-color: #ffe0;
    -ms-flex: var(--layout-flex-none_-_-ms-flex);
    -webkit-flex: var(--layout-flex-none_-_-webkit-flex);
    flex: var(--layout-flex-none_-_flex);
    height: 100px;
    width: 150px;
    background-size: 150px, 50px;
    background-image: url(https://steamuserimages-a.akamaihd.net/ugc/250338860238740863/3E6CDF0C040D0008A64A986D3127061321FF2BC0/);
}
    div#reason.style-scope.yt-player-error-message-renderer { 
        font-size: 25px;
        text-shadow: 1px 1px 3px #7b7b7b;
}
#reason.yt-player-error-message-renderer::after{
    content: '. Sorry about that.';
    font-size: 25px;
    text-shadow: 1px 1px 3px #7b7b7b;
}
yt-playability-error-supported-renderers {
    display: block;
    background: linear-gradient(#383838, #141518);
    -ms-flex: var(--layout-flex_-_-ms-flex);
    -webkit-flex: var(--layout-flex_-_-webkit-flex);
    flex: var(--layout-flex_-_flex);
    -webkit-flex-basis: var(--layout-flex_-_-webkit-flex-basis);
    flex-basis: var(--layout-flex_-_flex-basis);
    -ms-flex-direction: var(--layout-vertical_-_-ms-flex-direction);
    -webkit-flex-direction: var(--layout-vertical_-_-webkit-flex-direction);
    flex-direction: var(--layout-vertical_-_flex-direction);
}
.ytp-error .ytp-error-icon-container svg {
    max-width: 0;
    max-height: 100%;
}
.ytp-large-width-mode .ytp-error .ytp-error-content .ytp-error-content-wrap-subreason::after {
    font-size: 18px;
    content: ' Sorry about that.'
}
#error-page-hh-illustration {
    margin-bottom: 10px;
    background-image: none;
}
body {
    margin: 0;
    padding: 0;
    border: 0;
    font: 12px "YouTube Noto",Roboto,arial,sans-serif;
    background: #eaeaea;
    height: 100%;
}
}
