css:
@-moz-document domain("lafa.site") {
body, .shapka_new, .custom-tooltip-styling, .editorinput, .spl div, .mob-m.mob-m-logo {background: #222 !important;}
.cont_box, .left_col, .center_col, #profile_links_block div, .b_h, .c_c, .m_menu tr:first-child td, .logo td, .button_1613040694796, .block_table.l_c_td > tbody tr:nth-child(2) td > div:not([class="sidebar_dropdown_link"]), .detail_tbl tr td:first-child, .sidebar_list_link, .pagebr, .pager, form[action="/last_coment_users.php"] td, .colhead {background-color: #333 !important;}
.new_com_area, input:not([name="avatar"]), .highlight, tr:nth-last-child(n+2) td.colhead {background-color: #444 !important;}
.b_red-donate, .b_blue-o-site, .c_h, .new_notif td, .full_width, .b_blue, #tablesorter thead tr:first-child th, .colhead.header, .b_red-privet {background: #666 !important;}

a:hover, .ft_link:hover, #tablesorter thead tr:first-child th, #tablesorter td:first-child > span:last-child:hover, input:hover:not([name="avatar"]), .new_com_right_pane, .editorinput, .highlight, form[action="/last_coment_users.php"] td {color: white !important;}
a, body, .b_red-donate, .b_blue-o-site, .b_h, h2, .center_col > p, .center_col, .ft_link, .c_h, .button_1613040694796, .b_blue, .new_comm_time, .custom-tooltip-styling, .colhead, input:not([name="avatar"]), .spl div {color: #aaa !important;}
.btn_sw.btn_save a {color: #555 !important;}
#qs_form > div > table.subs_mail > tbody > tr > td:nth-child(1), .btn_sw.btn_save:hover a {color: black !important;}

.left_col td, .center_col > div, .cinema, .c_c, .cinema_box > tbody > tr:nth-child(2n) span, .new_notif td, .nomargin + div, #fav_div + div, .button_1613040694796, .new_com_separator, .sidebar_star_link, .sidebar_list_link, #tablesorter th, #tablesorter td, .new_com_area, .custom-tooltip-styling, .tooltip-separator, #reply_form_123456789 td, .spl div, form > table tr:first-child td, .main td, .filter_box > form > div, form[action="/last_coment_users.php"] td[style="background-color:"], form[action="/last_coment_users.php"] td hr, .b_h {border-color: #666 !important;}
.new_com_area.com-good, form[action="/last_coment_users.php"] td[style="background-color:#dff5df"] {border-color: #373 !important;}
.new_com_area.com-bad, form[action="/last_coment_users.php"] td[style="background-color:#fbe6e6"] {border-color: #733 !important;}

.new_notif td, .full_width, .c_pic_col img, .center_col > center > div, .new_com_area, #tablesorter, .container a img, .lazy {border-radius: 4px !important;}
.left_col tbody tr:first-child td, .block_table tbody tr td, tr:first-child > td.colhead {border-radius: 4px 4px 0 0 !important;}
.left_col tbody tr:nth-child(2) td, .cinema tbody tr td, .b_h {border-radius: 0 0 4px 4px !important;}
.cinema, #tablesorter {border-collapse: separate !important;}
#tablesorter thead tr:first-child th:first-child {border-radius: 4px 0px 0px 0px !important;}
#tablesorter thead tr:first-child th:last-child {border-radius: 0px 4px 0px 0px !important;}
#tablesorter tbody tr:nth-last-child(2) td:first-child {border-radius: 0px 0px 0px 4px !important;}
#tablesorter tbody tr:nth-last-child(2) td:last-child {border-radius: 0px 0px 4px 0px !important;}
.container > div {border-radius: 0px 4px 0px 4px !important;}
#tablesorter td {border-top: 0 !important;}
#tablesorter td:nth-child(n+2) {border-left: 0 !important;}

#profile_links_block {filter: hue-rotate(180deg);}
.back_top {filter: hue-rotate(20deg);}
.subs_box {filter: invert(1) hue-rotate(180deg) contrast(70%) brightness(140%) saturate(250%);}
.subs_mail > tbody > tr > td:nth-child(2) > a > img {filter: hue-rotate(240deg) saturate(700%) brightness(150%);}
.c_like_up, .c_like_down {filter: invert(1);}
#tablesorter > tbody > tr > td:nth-last-child(2) > a > img, .btn_sw.btn_save {filter: invert(80%) hue-rotate(170deg) saturate(200%);}
.lazy[data-original="/pic/no_avatar.png"] {filter: invert(80%);}
.ratings_stars {filter: brightness(50%);}
.ratings_stars.ratings_vote, .ratings_stars.ratings_over {filter: brightness(1);}
.c_like.c_like_up:hover, .c_like_up_selected {filter: hue-rotate(260deg);}
.c_like.c_like_down:hover, .c_like_down_selected {filter: hue-rotate(140deg);}

.cont_box, .logo td {width: 1078px !important; box-shadow: 0 0 8px black; border-radius: 5px;}
@media screen and (min-width: 1091px) {
  .m_menu {width: 1078px !important; border-radius: 4px;}
}
.td_menu {border-radius: 4px 0 0 4px;}
.m_menu > tbody > tr > td:last-child {border-radius: 0 4px 4px 0;}
.left_col {padding-left: 10px !important; border-radius: 4px;}
.center_col {padding-right: 10px !important; border-radius: 4px;}
.m_menu tr:first-child {box-shadow: 0 0 8px black; border-radius: 5px;}
.logo td {margin-bottom: 10px !important; height: 112px !important;}

.container a img {box-shadow: 0 0 8px #2a2a2a !important; padding: 0 !important;}

.container, .new_notif, .main {background: none !important;}
.container {height: 222px !important;}

.new_notif td, .center_col div td, .another_film td, .editorinput, .in_cinema td, .pagebr, .index, .filter_box, .no_block td, #tablesorter thead tr:first-child td, form[action="/last_coment_users.php"] td[style="background-color:#ccc;"] {border: none !important;}
input[type="button"], input[type="submit"], input[type="reset"] {border: 1px #666 !important; padding: 6px !important;}
.bottom {padding: 10px 0 0 5px !important;}
.dark_mode_toggle {display: none !important;}

.b_h {padding: 9px; display: block;}
.left_col, .center_col {padding-top: 10px;}
.gallerycontainer {max-width: calc(100% - 40px);}

.sidebar_dropdown_link {border-color: #666 !important; background-color: #333 !important; background-image: url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' xml:space='preserve' width='15px' height='15px' version='1.1' style='shape-rendering:geometricPrecision; text-rendering:geometricPrecision; image-rendering:optimizeQuality; fill-rule:evenodd; clip-rule:evenodd' viewBox='0 0 8.52 8.52' xmlns:xlink='http://www.w3.org/1999/xlink' xmlns:xodm='http://www.corel.com/coreldraw/odm/2003'%3e%3cdefs%3e%3cstyle type='text/css'%3e%3c![CDATA[ .fil0 %7bfill:%23FFD500%7d ]]%3e%3c/style%3e%3c/defs%3e%3cg id='Слой_x0020_1'%3e%3cmetadata id='CorelCorpID_0Corel-Layer'/%3e%3cpath class='fil0' d='M1.7 0l5.11 0c0.94,0 1.7,0.77 1.7,1.7l0 5.11c0,0.94 -0.77,1.7 -1.7,1.7l-5.11 0c-0.94,0 -1.7,-0.77 -1.7,-1.7l0 -5.11c0,-0.94 0.77,-1.7 1.7,-1.7zm0.85 1.54l1.96 0 1.45 2.72 -1.45 2.72 -1.96 0 1.45 -2.72 -1.45 -2.72z'/%3e%3c/g%3e%3c/svg%3e") !important;}
.sidebar_dropdown_link span {color: white !important;}

@media screen and (max-width: 1091px) {
    .shapka_new2 .m_menu td a, .shapka_new .m_menu td a {border-color: #666 !important;}
    .shapka_new2 .m_menu td a:hover, .shapka_new .m_menu td a:hover {background: #444 !important;}
    .shapka_new2 .m_menu, .shapka_new .m_menu {background: #222 !important;}
    .mob-m {box-shadow: none !important;}
}
}
