body {
    margin: 0;
    font-family: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,"Helvetica Neue",Arial,sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol";
    font-size: .9375rem;
    font-weight: 400;
    line-height: 1.5;
    color: #cecece;
    text-align: left;
    background-color: #353535;
}

[data-region="drawer"] {
    position: fixed;
    padding: 20px 20px;
    width: 285px;
    top: 50px;
    height: calc(100% - 50px);
    overflow-y: auto;
    -webkit-overflow-scrolling: touch;
    z-index: 999;
    background-color: #353535;
    -webkit-transition: right .5s ease,left .5s ease;
    -moz-transition: right .5s ease,left .5s ease;
    -o-transition: right .5s ease,left .5s ease;
    transition: right .5s ease,left .5s ease;
}

#header .navbar-nav button, #header .navbar-nav button:hover, #header .navbar-nav button:focus {
    color: #1a1a1a;
    border-radius: 4px;
}

.block-region .block .card-body .card-title, .block-region .block .userprofile .profile_tree section .card-title, .userprofile .profile_tree .block-region .block section .card-title {
    width: 100%;
    padding: 20px;
    background: #353535;
    border-bottom: 1px solid #d5d5d5;
    display: block!important;
    margin-right: 0!important;
}

.fp-site-customdesc {
    background: #353535;
    padding: 25px 0;
    text-align: center;
}

.fp-site-customdesc p {
    line-height: 22px;
    color: #fff!important;
    margin-bottom: 0;
}

.list-group-item {
    position: relative;
    display: block;
    padding: .75rem 1.25rem;
    margin-bottom: -1px;
    background-color: #353535;
    border: 1px solid rgba(0,0,0,.125);
}

.list-group-item-action {
    width: 100%;
    color: #f7f7f7;
    text-align: inherit;
}

#footer .infoarea>p {
    color: #cecece;
    line-height: 22px;
    margin-top: 45px;
    margin-bottom: 0;
}

#logo span {
display: block;
-moz-box-sizing: border-box;
box-sizing: border-box;
background: url(https://i.ibb.co/T4wqPkN/152594758597720led-3-jpg-250-135-jpg-250-135.jpg) no-repeat;
width: 250px;
height: 135px;
padding-left: 250px;
}

.header-main .header-main-content .navbar-brand .logo img {
    width: 0%;
}

#footer {
    background: #353535!important;
    border-top: 5px solid #ed423b;
    margin: 30px 0 0;
    padding: 0!important;
}

.footer-logo {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://i.ibb.co/T4wqPkN/152594758597720led-3-jpg-250-135-jpg-250-135.jpg) no-repeat;
    width: 220px;
