    border-bottom: 1px solid rgba(0,0,0,.125);
}

.modal-content, .moodle-dialogue-base .moodle-dialogue-wrap.moodle-dialogue-content {
    position: relative;
    display: flex;
    flex-direction: column;
    width: 100%;
    pointer-events: auto;
    background-color: #353535;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid rgb(255, 255, 255);
    outline: 0;
}

.list-group-item {
    position: relative;
    display: block;
    padding: .75rem 1.25rem;
    margin-bottom: -1px;
    background-color: #353535;
    border: 1px solid #a0a0a0;
}

.header-main .navbar .navbar-inner .nav-item .dropdown-menu a {
    color: #ed423b;
    border-bottom: 1px solid #e6e6e6;
    background: #353535;
    white-space: normal;
    padding-top: .5rem;
    padding-bottom: .5rem;
}

#header .menubar .dropdown-menu a {
    color: rgb(0 0 0);
    background: #383838;
    border-bottom: 1px solid #000000;
}

#footer .footer-foot p {
    color: #cccccc;
    line-height: 20px;
    padding: 6px 0;
    border-top: 1px solid #ed423b;
    margin-bottom: 0;
}

.card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
    position: relative;
    display: flex;
    flex-direction: column;
    min-width: 0;
    word-wrap: break-word;
    background-color: #353535;
    -webkit-background-clip: border-box;
    background-clip: border-box;
    border: 1px solid #fff;
}

table.flexible {
    color: #f1f1f1;
}

label {
    font-size: 16px;
    color: #cecece;
    vertical-align: middle;
}
.popover-region-container {
    opacity: 1;
    visibility: visible;
    position: absolute;
    right: 0;
    top: 0;
    height: 500px;
    width: 380px;
    border: 1px solid #ddd;
    -webkit-transition: height .25s;
    -o-transition: height .25s;
    transition: height .25s;
    background-color: #353535;
    z-index: 1;
}
.popover-region-footer-container {
    height: 30px;
    text-align: center;
    border-top: 0 solid #ddd;
    background-color: #353535;
    padding-top: 3px;
}

.generaltable tbody>tr:nth-child(2n + 1)>td, .generaltable tbody>tr:nth-child(2n + 1)>th {
    background: #353535;
}

.generaltable {
    color: #dedede;
}

.generaltable tbody tr:hover>td, .generaltable tbody tr:hover>th {
    background: #aaa;
}

.courses .coursebox:hover {
    background: #353535;
}
