css:
@-moz-document domain("marciniwuc.com") {
/*
 * dark reader mode
 * START
 */

body {
    background-color: #222222;
}
blockquote {
    background-color: #333333;
    border-top: 2px solid #555555;
}
h1, h2, h3, h4,
p,
ol li, ol li span, ul li, ul li span,
.slider-wrapper input {
    color: #cccccc !important;
}
/* linki */
.elementor-kit-30817 a {
    color: #5e9dee;
}
a:visited {
    color: #b083ff;
}
/* navbar */
body > div.elementor-location-header > div > section:nth-child(3) div * {
    background-color: #333333;
    color: #cccccc !important;
}

/* komentarze - nicki */
#wpdcom .wpd-blog-post_author .wpd-comment-author, #wpdcom .wpd-blog-post_author .wpd-comment-author a {
    color: #D3544A !important;
}
#wpdcom .wpd-blog-user .wpd-comment-author, #wpdcom .wpd-blog-user .wpd-comment-author a {
    color: #31839e !important;
}
#wpdcom .wpd-blog-guest .wpd-comment-author, #wpdcom .wpd-blog-guest .wpd-comment-author a {
    color: #cccccc !important;
}
/* komentarze - podświetlenie komentarza pod kursorem */
#wpdcom .wpd-comment .wpd-comment-wrap:hover {
    background-color: #555555;
    border-radius: 28px;
}
#wpdcom .wpd-comment-header .wpd-comment-link {
    padding-right: 16px;
}

/*
 * dark reader mode
 * STOP
 */

/*
 * hiding elements in reader mode
 * START
 */

/* hide top logo */
body > div.elementor-location-header > div > section:nth-child(2) {
    display: none !important;
}

/* hide right col */
div[data-elementor-type="header"] ~ div div.elementor-container.elementor-column-gap-default > div.elementor-row > div:first-child {
    width: 100% !important;
}
div[data-elementor-type="header"] ~ div div.elementor-container.elementor-column-gap-default > div.elementor-row > div:not(:first-child) {
    display: none !important;
}

/* hide rankingi ad */
#post-content > div.elementor-widget-container > div.rankingi-css2 {
    display: none !important;
}

/* hide bottom ad */
#elementor-popup-modal-33572 {
    display: none !important;
}

/* komentarze - tryb kompaktowy */
#wpdcom { /* komentarze na całej szerokości strony */
    max-width: 100%;
}
#wpdcom .wpd-comment .wpd-comment-header { /* usuń marginesy poziome wokół nicku */
    padding: 0;
}
#wpdcom .wpd-comment-text { /* zawartość komentarza - ramka, marginesy, wysokość linii tekstu */
    border: 1px solid #e6e6e6;
    border-width: 0 0 0 1px;
    padding: 0 10px 0 10px;
    line-height: 1;
}
#wpdcom .wpd-comment-footer { /* bez stopki/linku "Odpowiedz" pod komentarzem */
    display: none !important;
}

/*
 * hiding elements in reader mode
 * STOP
 */
}
