css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") 

{
	#h-sec, #h-pri, #search, #announce, .side-container, .sidebar, .user-info, .more, .share, .footer-contents, #footer, .copyright, .ad,.story-list, .related-stories, h2, #email, #facebook, #twitter, .whoduggthis, .made-popular,.news-buried
	{
	   display:none!important;
	}
	#h
	{
	   height: 43px !important;
	   margin-top: -5px !important;
	   margin-bottom: 0px!important;
	}
	.side-header
	{
	   float:left!important;
	   margin-left: 10px!important;
	}
	#wrapper, #contents, .corpus-wrapper, #container, .corpus-contents
	{
	   background: #fff!important;
	   margin:0px !important;
	   padding-top: 0px !important;
	}
	.main
	{
	   width: 100%!important;
	}
	.body:hover, .body
	{
	   color: #000!important;
	}
	.c-menu
	{
	 padding-bottom: 3px !important;
	}
}
