@namespace url(http://www.w3.org/1999/xhtml);

Credits[Strecher] nnm.ru {}

@-moz-document domain("nnm.ru") {
div[id="top-banner"] { display: none !important; }
div[id="rightMenu"] { display: none !important; }
div[id="bottom-banner"] { display: none !important; }
div[class="left-banner"] { display: none !important; }
div[class="center-banner"] { display: none !important; }

div[class="new-text"] { width: 95% !important; background-color:#f7f7f7 !important; border:1px solid #aaa !important; padding:10px !important; margin-right:10px !important; margin-top:10px !important; margin-bottom:10px !important; margin-left:0px !important; }

div[id="page"] { width: 100% !important; background-color: transparent !important; border:0px !important; }

div[id="new"] { width: 95% !important; }
div[class="toolbar"] { width: 95% !important; }
div{ width: 100% !important; text-align: justify !important;}
div[class="listing"] { width: 95% !important; }
ul { width: 90% !important;  margin-left:10px !important;}
ul [class="new-menu"] { width: 95% !important; }

div[id="leftMenu"] { width: 80% !important; }

ul [id="topmenu"] { background-color:#000 !important; }

ul [class="new-admin"] { width: 200px !important; border:1px dotted #aaa !important; }

div[class="tags"] { width: 92% !important; padding:8px !important; color:#bbb !important; }
div[class="tags"] a { color:#000 !important; text-decoration:none !important; }

img[class="new-image-center"] { margin-top:8px !important; border:1px solid #aaa !important; padding:0px !important; }

div[class="docinfo"] { background-color:#f7f7f7 !important; border:1px solid #aaa !important; margin:10px !important; width:90% !important; }

div[id="topmenu"] { background-color:#333 !important; margin-left:0px !important; width:100% !important; }
div[class="toolbar"] { background-color:#333 !important; margin-left:0px !important; border:0px !important; width:100% !important; }
div[class="content"] { border:0px !important; }
div[class="komment"], div[class="info"], div[class="moder"] { width:95% !important; }
div[class="level-2"] { width: 92% !important; background-color:#f7f7f7 !important; border:1px dotted #aaa !important; padding:10px !important; }

div[id="leftmenu"] { background-color:#f7f7f7 !important; margin:20px !important; width:95% !important; border:1px solid #aaa !important; text-align:justify !important; }

div[class="tagdoc"] { width:95% !important; border:1px solid #aaa !important; }

div[class="komment"] { width: 92% !important; background-color:#f7f7f7 !important; border:1px solid #aaa !important; padding:10px !important; }

li[class="auth"] { display:none !important; }

div[class="level-2"] span { padding-right:2000px !important; padding-bottom:10px !important; }
div[class="komment"] span { padding-right:2000px !important; padding-bottom:10px !important; }

div[id="searchform"] { width:200px !important; }

div[class="zakladki"] { width:100% !important; background-color:#f0f0f0 !important; border-bottom:1px solid #000 !important; }
div[class="zakladki"] ul { width:100% !important; border-bottom:0px solid #000 !important; }

div[class="kommbb"] ul.bbtag { margin-left:0px !important; }

textarea { width:95% !important; border:1px dashed #aaa !important; padding:5px !important; }
}
