css:
@-moz-document domain(lurkmore.to) {

body {
    background: #2b2b2b;
}

#content {
    background: #2b2b2b;
    color: #b2b2b2;
}

a {
    color: #0099aa;
}

table {
    color: #b2b2b2;
}

h1, h2, h3, h4, h5, h6 {
    color: #b2b2b2;
}

element {
    background: #F00 !important;
    background-color: rgb(2, 2, 2);
}

#toc, .toc, .mw-warning {
    background: #4b4b4b;
    color: #bababa;
}

table.wikitable, table.prettytable {
    background: #4b4b4b;
    color: #bababa;
}

.element {
    clear: both;
    width: 80%;
    background-color: #222 !important;
    border-color: #AAB #AAB #AAB #999;
    border-style: solid;
    border-width: 1px 1px 1px 10px;
    padding: 4px;
    margin: 1px auto;
    vertical-align: middle;
    text-align: left;
}

.pBody {
    background-color: #2d2d2d;
    color: #d2d2d2;
}
}
