css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://userstyles.org") , url-prefix("https://userstyles.org") {

body {
  font-family: Gill Sans, Gill Sans MT, Helvetica Neue, Arial, sans-serif !important;
  margin: 0 !important;
  font-weight: 200;
  padding-bottom: 40px;
}


body > * {
  padding-left: 60px !important;
  max-width: 720px;
}


#breadcrumbs {
  margin: 0 !important;
  padding: 5px 0 5px 60px !important;
  background: #68A;
  color: #AAC;
  border-bottom: 1px solid hsla(0,0%,100%, .2);
  width: auto;
  font-weight: normal;
  max-width: none;
}

#other-links {
  color: #AAC;
  top: 2px !important;
  left: 430px !important;
  font-weight: normal;
}

#breadcrumbs a, #other-links a {
  color: #CCF !important;
  text-decoration: none !important;
}
#breadcrumbs a:hover, #other-links a:hover { color: #FF8 !important; }


h1 {
  margin: 0 0 30px !important;
  padding: 10px 0 10px 60px !important;
  -moz-box-shadow: -11px 3px 10px hsla(0,0%,0%,.3);
  -webkit-box-shadow: -11px 3px 10px hsla(0,0%,0%,.3);
  background: #579 !important;
  font-variant: normal !important;

  color: #FFF;
  font-weight: 200;
  width: auto;
  max-width: none;
}

#style-show {
  width: 640px;
  -moz-column-count: 0 !important;
  margin: 0 !important;
  color: #333;
  font-weight: 200;
  line-height: 1.3em;
}


#install {
  float: none !important;
  margin: 0 !important;
  position: absolute !important;
  left: 740px;
  border: 4px solid #FFF !important;
  background: hsla(120, 30%, 85%, 1) !important;
  -moz-box-shadow: 0px 3px 8px hsla(0,0%,0%,.2);
  -webkit-box-shadow: 0px 3px 8px hsla(0,0%,0%,.2);
  -moz-border-radius: 0 !important;
  width: 200px !important;
}

h2 {
  color: #333;
  font-style: normal !important;
  text-transform: uppercase;
  font-weight: normal !important;
  letter-spacing: 0.1em;

}

h3 {
  color: #333;
  font-style: normal !important;
  font-weight: normal !important;
}

#install h2, #sidebar h2 {
  font-size: 14px;
}

#install ul {
  list-style: none;
  padding: 0 !important;
}

#style-meta {
  font-family: Georgia, serif;
  font-size: 13px !important;
  padding-bottom: 10px;
  border-bottom: 2px dotted #BBB;
  position: relative;
  font-variant: small-caps;
}

#install-counts {
  font-variant: normal;
  font-style: italic;
}

#detail-average-rating { 
  position: absolute;
  right: 0;
  top: 0;
}

.screenshot {
  border: 1px solid #BBB !important;
  -moz-box-shadow: 0px 3px 8px hsla(0,0%,0%,.2);
  -webkit-box-shadow: 0px 3px 8px hsla(0,0%,0%,.2);
}

.screenshot-caption {
  font-family: Georgia, serif;
  font-style: normal !important;
  font-weight: 600 !important;
  text-transform: uppercase;
  letter-spacing: 0.2em;
  color: #777;
  font-size: 12px;
}

h2#feedback {
  font-weight: 400;
  font-size: 18px;
  text-transform: uppercase;
  letter-spacing: 0.2em;
  margin: 20px 0 10px !important;
  padding: 10px 0 0 !important;

  border-top: 2px dotted #BBB;
}

h2#feedback + ul {
  list-style: none;
  margin: 0 !important;
  padding: 0 !important;

  border-bottom: 2px dotted #BBB;
}


/**** Front page ****/

#sidebar {
  float: left !important;
  margin: 0 60px 0 0 !important;
  width: 280px;
}

#sidebar  a {
  text-decoration: none !important;
  display: block;
  padding: 2px 0 ;
  border-bottom: 1px dotted #CCC;
}
#sidebar a:hover {
  background: #FFD;
  border-color: #666;
}

#sidebar li a { 
}



#sidebar ul {
  margin: 0 0 1.0em !important;
  padding: 0;
  list-style: none;
  font-size: 13px;
}

}
