css:
@-moz-document domain("w3schools.com") {
.top, div, .w3-white, a, html, table.browserref th {
    color: #ccc !important;
    background-color: #333 !important;
}

.w3-hover-white:hover, a:hover{
    color: #ccc !important;
    background-color: #555 !important;
}

.w3-table-all tr:nth-child(2n+1) {
    color: #ccc !important;
    background-color: #333 !important;
}

.w3-table-all tr:nth-child(2n) {
    color: #ccc !important;
    background-color: #444 !important;
}
.w3-button {
    color: #000 !important;
    background-color: #777 !important;
}
.w3-button:hover {
    color: #000 !important;
    background-color: #abd9ac !important;
}

.w3-text-dark-grey, .w3-hover-text-dark-grey:hover, .w3-text-dark-gray, .w3-hover-text-dark-gray:hover {
    color: #b7b7b7 !important;
    background-color: #555 !important;
}
.w3-light-grey, .w3-hover-light-grey:hover, .w3-light-gray, .w3-hover-light-gray:hover {
    color: #d7d7d7 !important;
    background-color: #555 !important;
}

.w3-codespan {
    color: #f03;
    background-color: #000 !important;
}
table.browserref tr:nth-child(2n+1) {
    color: #ccc !important;
    background-color: #333 !important;
}

table.browserref tr:nth-child(2n) {
    color: #ccc !important;
    background-color: #444 !important;
}

.w3-example{
    color: #000 !important;
    background-color: #4a4a4a !important;
}
}
