@-moz-document domain("dafont.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#menuthemes table,

.donate,
.menupselect,

:focus,
h1
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

table {
    display: inline-block;
    margin-left: -8rem;
}

/* Fonts 
_____________________________________________________________________________________________________*/

#menuthemes a
{
	font-size: 19px!important;
}

body, div, td, p, select
{
	font-size: 17px!important;
}

#menuthemes a,

body,
div,
li,
p,
select,
td
{
	line-height: 23px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#adcontainer1,
#sky,

.adsbygoogle,
.native-ad,
.notranslate,

[id*='ad'],
[style="width:728px; height:101px; margin-top:14px; overflow:hidden"]
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

table {
    display: inline-block;
    margin-left: -1rem;
}

[style="margin-left:140px;display:block"]
 {
    margin-left: 25px!important;
}

[style="width:120px;height:600px;float:left;margin-right:20px"] {
	margin-right: -33px!important;
}


/* Margins 
_____________________________________________________________________________________________________*/

.container
{
	margin: 1.2rem!important;
}

input
{
	margin: 3px 6px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#menuthemes,

.preview,

[style="margin-left:140px"]
{
	padding: 1.2rem!important;
}

input
{
	padding: 3px 6px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

table td {
    width: 1%;
}


[style="width:120px;height:600px;float:left;margin-right:20px"] {
	width: 1px!important;
}
}

