css:
/*
 * LostForum Siyah Tema
 *
 * Author      : Supercem
 * Updated     : July 26th, 2009
 * Website     : http://supercem88.blogspot.com/
 * Description : Modifies the colors on www.lostforum.gen.tr
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.lostforum.gen.tr"),
url-prefix("http://lostforum.gen.tr"),
url-prefix("http://lostforum.gen.tr/index.php/"),
url-prefix("www.lostforum.gen.tr/index.php/")
 {


/* Normal, standard links. */
a:link
{
	color: #000000 !important;
	text-decoration: underline;
}
a:visited
{
	color: #484848 !important;
	text-decoration: underline;
}
a:hover
{
	color: #afeeee !important;
	text-decoration: underline;
}
/*         1111111111111111111111111111111111111111111111111111111111111111111         */
/* Navigation links - for the link tree. */
.nav, .nav:link, .nav:visited
{
	color: #000000 !important;
	text-decoration: none;
}
a.nav:hover
{
	font-weight: bold;
	color: #000000 !important;
	text-decoration: underline;
}

/* Tables should show empty cells. */
table
{
	empty-cells: show;
}

/* By default (td, body..) use Tahoma in black. */
body, td, th,tr
{
        background-color: transparent !important;
	color: #000000 !important;
	font-size: small;
	font-family: segoe ui, sans-serif !important;
}

/* The main body of the entire forum. */
body
{
	background: transparent !important;
        background-color:transparent !important;
	margin: 0px;
	padding: 0px;
}

/*222222222222222222222222222222222222222222222222222222222222222*/
/* Input boxes - just a bit smaller than normal so they align well. */
input, textarea, button
{
	font-size: 9pt;
	color: #000000 !important;
	font-family: segoe ui, sans-serif !important;
        background-color: transparent !important;
}

/* All input elements that are checkboxes or radio buttons. */
input.check
{
}

/* Selects are a bit smaller, because it makes them look even better 8). */
select
{
	font-size: 8pt;
	font-weight: normal;
	color: #000000 !important;
	font-family: tahoma, sans-serif;
}

/* Standard horizontal rule.. ([hr], etc.) */
hr, .hrcolor
{
	height: 1px;
	border: 0;
	color: #000000 !important;
	background-color: none !important;
}
/**2.123122222222222222222222222222222222222222222222214124124/
/* A quote, perhaps from another post. */
.quote
{
	color: #000000 !important;
	background-color: transparent !important;
	border: 1px solid #5588aa !important;
	margin: 1px;
	padding: 1px;
	font-size: x-small;
	line-height: 1.4em;
}

/* A code block - maybe even PHP ;). */
.code
{
	color: #000000 !important;
	background-color: #cccccc;
	font-family: "courier new", "times new roman", monospace;
	font-size: x-small;
	line-height: 1.3em;
	/* Put a nice border around it. */
	border: 1px solid #5588aa !important;
	margin: 1px auto 1px auto;
	padding: 1px;
	width: 99%;
	/* Don't wrap its contents, and show scrollbars. */
	white-space: nowrap;
	overflow: auto;
	/* Stop after about 24 lines, and just show a scrollbar. */
	max-height: 24em;
}

/* The "Quote:" and "Code:" header parts... */
.quoteheader, .codeheader
{
	color: #000000 !important;
	text-decoration: none;
	font-style: normal;
	font-weight: bold;
	font-size: x-small;
	line-height: 1.2em;
}

/* Generally, those [?] icons.  This makes your cursor a help icon. */
.help
{
	cursor: help;
}

/* /me uses this a lot. (emote, try typing /me in a post.) */
.meaction
{
	color: red;
}

/* The main post box - this makes it as wide as possible. */
.editor
{
	width: 96%;
}

/* Highlighted text - such as search results. */
.highlight
{
	background-color: yellow;
	font-weight: bold;
	color: #000000 !important;
}
/*4444444444444444444444444444444444444444444444444444444444444444444444444444444444444444444444444*/
/* Alternating backgrounds for posts, and several other sections of the forum. */
.windowbg
{
	color: #000000 !important;
	background-color: transparent !important;
        border: 1px solid #2F4F4F !important;

}
.windowbg2
{
	color: #000000 !important;
	background-color: transparent !important;
border: 1px solid #5588aa !important;
}

/* These are used primarily for titles, but also for headers (the row that says what everything in the table is.) */
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg a:link, .titlebg a:visited, .titlebg2, tr.titlebg2 th, tr.titlebg2 td, .titlebg2 a:link, .titlebg2 a:visited
{
	color: #000000 !important;
	font-style: normal;
	background-color: transparent !important;
}
.titlebg a:hover, .titlebg2 a:hover
{
	color: #000000 !important;
}

/* This is used for categories, page indexes, and several other areas in the forum. */
.catbg, .catbg3
{
	background-image: transparent !important;
        background: transparent !important;
}

/* This is used for a category that has new posts in it... to make it light up. */
.catbg2
{
	background-image: transparent !important;
        background: transparent !important;
}

.catbg, .catbg2, .catbg3
{
	font-weight: bold;
	background-color: transparent !important;
	color: #000000 !important;
}

/*55555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555*/
/* This is used for tables that have a grid/border background color (such as the topic listing.) */
.bordercolor
{
	background-color: transparent !important;
}

/* This is used on tables that should just have a border around them. */
.tborder
{
	border: 1px solid #000000 !important;
	background-color: transparent !important;
	padding: 2px;
}

/* Default font sizes: small (8pt), normal (10pt), and large (14pt). */
.smalltext
{
	font-size: x-small;
	font-family: tahoma, sans-serif;
}
.normaltext
{
	font-size: small;
}
.largetext
{
	font-size: large;
}

/* This is the white header area where the title, menu bars, and header boxes are. */
#headerarea
{	background-color: transparent !important;
        background-image: url('http://img233.imageshack.us/img233/4403/978629fogsea21024.jpg') !important;
	border-bottom: 1px solid #808080 !important;
}
/* This is the main area of the forum, the part that's gray. */
#bodyarea
{
	background-image:
        url('http://img233.imageshack.us/img233/4403/978629fogsea21024.jpg') !important;
        background-repeat:repeat-x !important;
        background-attachment:fixed !important;

}
/* And this is the bottom, where the copyright is, etc. */
#footerarea
{
	color: #cccccc !important;
	background-color: transparent !important;
        background-image: url('http://img233.imageshack.us/img233/4403/978629fogsea21024.jpg') !important;
	border-top: 1px solid #5588aa !important;
}

/* This is for the special header boxes on the top (user info, key stats, news box.) */
.headertitles
{
	background-color: #808080 !important;
	border: 1px solid #5588aa !important;
	height: 12px;
}
.headerbodies
{
	border: 1px solid #5588aa !important;
	background-color: transparent !important;
	background-repeat: no-repeat !important;
	background-position: bottom;
}

/* Posts and personal messages displayed throughout the forum. */
.post, .personalmessage
{
	width: 100%;
	overflow: auto;
	line-height: 1.3em;
}

/* All the signatures used in the forum.  If your forum users use Mozilla, Opera, or Safari, you might add max-height here ;). */
.signature
{
	width: 100%;
	overflow: auto;
	padding-bottom: 3px;
	line-height: 1.3em;
}
/* No image should have a border when linked */
a img{
	border: 0;
}

/* The AJAX notifier */
#ajax_in_progress
{
	background: transparent !important;
	color: #000000 !important;
	text-align: center;
	font-weight: bold;
	font-size: 18pt;
	padding: 3px;
	width: 100%;
	position: fixed;
	top: 0;
	left: 0;
}
}
