}
/* Header */
#header-wrap, .search-input{
    background: #25252d !important;
    color: #c1c1c1 !important;
    
}
.queryMessage{
    margin-top:-30px;
    margin-bottom:10px;
    background: #0002;
    border-radius: 4px;
    color: #CCC;
}
.queryMessage a{color: #FFF !important;}
/* Body */
body {
    font-family: Tajawal, Tahoma;
    background: #2a2a2d !important;
}
.tags-wrapper .menu-cat li a {
    background-color: #40404c;
}
.tags-wrapper .menu-cat li a:hover {
    background-color: #656579;
}
.widget > h3.home-title {
    background: #2a2a2d;
    box-shadow: 0 1px 1px #0004;
}
#outer-wrapper{
    background: #ffffff05 !important;
    max-width: 100vw;
    overflow-x:hidden;
}
#content-wrapper {
    width: 100%;
}
.index-post-wrap {
    justify-content: center;
}
.index-post {
    width: 250px;
}
.index-post .post-image-wrap {
    border: 2px solid #ffffff26;
    box-shadow: 0 2px 3px #0004;
    border-radius: 2px;
    overflow: hidden;
}
.index-post .post-info,
.index-post .post-image-wrap .post-image-link,
.index-post .post-image-wrap .post-image-link .post-thumb {
    border-radius: 0px !important;
}
.post-info >h2{
    font-family: Tajawal, Tahoma !important;
    font-size: 16px !important;
    height: 44px;
    overflow: hidden;
}
.index-post .post-info {
    font-family: 'Tajawal';
    background-image: linear-gradient(rgb(0 0 0 / 0%),#000);
    padding: 10px;
    padding-top: 50px;
    text-shadow: 0px 0px 4px #000;
}
/* Footer */
.back-top{
    background: #c55d18;
}
.blog-pager span.page-dots {
    width: 32px;
    background-color: #5558;
    color: #CCC;
}
.blog-pager a, .blog-pager span {
    background-color: #5558;
    color: #CCC;
}
.blog-pager a:hover {
    background-color: #555F;
    color: #fff;
}
#mycontent{
    color: #999 !important;
}
#menu-footer ul li a:hover {
    color: #ffdda9;
}
}
