css:
@-moz-document domain("languagecrush.com")
{
 .navbar
  {
    background-color: #333 !important;
}

.navbar .nav-link
  {
    color: #f5f5f5 !important;
}

.btn-secondary
 {
   background-color: transparent !important;
}

/** Reading tool - Specific **
/* default / known word*/

/* Unknown reading word */
  .reading-word-container .bg-deep-orange
  {
    background-color: #f4d6d6 !important;
    color: #08141a !important;
    border-radius: 5px;
  }

  .reading-lesson-container .badge.badge-pill.bg-deep-orange
  {
    background-color: #f4d6d6 !important;
    color: #08141a !important;
  }

  .word-definitions-container .bg-deep-orange
  {
    background-color: #f4d6d6 !important;
  }

  .word-definitions-translations-container .bg-deep-orange
  {
    background-color: #f4d6d6 !important;
  }

/* Looked up / Learning reading word*/
  .reading-word-container .bg-success
  {
    background-color: #C3EFC3 !important;
    border-radius: 5px;
  }

  .reading-lesson-container .badge.badge-pill.bg-success
  {
    background-color: #C3EFC3 !important;
    color: #08141a !important;
  }

  .word-definitions-container .bg-success
  {
    background-color: #C3EFC3 !important;
    color: #08141a !important;
  }

  .word-definitions-translations-container .bg-success
  {
    background-color: #C3EFC3 !important;
    color: #08141a !important;
  }

  .word-definitions-translations-container .bg-success:hover
  {
    background-color: #C3EFC3 !important;
    color: #08141a !important;
  }
}
