css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

a{color: #D84FB3 !important}
.double_arrow img{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -5px -56px !important}
div.tokenizer .token:hover span.x_hover{background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -842px -64px !important;cursor:pointer;display:inline;padding:0 6px 0 5px}
.share_and_hide .share_a{float:left;padding:0 14px 1px 4px;background:white url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat right -355px !important;border:solid 1px #7f93bc}
.s_and_h_big .share_a{background:white url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat right -274px !important;padding:1px 18px 2px 4px}
.share_and_hide .x_to_hide{border:none;width:3px;padding:1px 5px 2px;margin-left:3px;background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -844px -65px !important}
.share_and_hide .x_to_hide:hover{background:#3B5998 url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -871px -65px !important}
#presence #chat_next_tab span{background-repeat:no-repeat;background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-position:-820px -75px;width:4px;height:8px;margin:8px 2px 0 0;float:left}
#presence #chat_previous_tab span{float:right;background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-position:-826px -75px}
#presence #chat_next_tab.disabled span{background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-position:-820px -66px}
#presence #chat_previous_tab.disabled span{background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-position:-826px -66px}
#chat_tab_bar .tab_handle.highlight{color:#fff;margin:0;height:27px;padding:2px 0 0;border-color:#3b5998;background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-position:0 -134px;background-repeat:repeat-x}
#chat_tab_bar .tab_count{position:absolute;top:-3px;right:12px;padding-top:1px;font-weight:bold;text-align:center;width:18px;height:16px;font-size:9px;color:#fff;display:none;background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-position:-801px -66px;background-repeat:no-repeat}
#chat_tab_bar .tab_button_div .tab_name .tab_availability{width:7px;height:7px;background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -715px -101px !important;margin:0 0 1px 4px}
#chat_tab_bar .notification_x{margin:9px 4px 0 0;height:10px;width:9px;color:#666;font-weight:bold;text-align:center;cursor:pointer;background:url('http://img193.imageshack.us/img193/6769/toppink.png') -870px -68px !important}
#chat_tab_bar .chat_header .header_buttons .minimize{float:right;display:block;height:15px;width:15px;background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -711px -58px !important}
#chat_tab_bar .chat_header .header_buttons .close{float:right;display:block;height:15px;width:15px;background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -843px -63px !important}
#chat_tab_bar img.chat_info_pic{position:absolute;top:11px;left:13px;width:45px;height:45px;padding:0 1px 1px 0;background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -480px -166px !important}
#chat_tab_bar .chat_window .chat_input_div .chat_input_border{background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -590px -120px !important;width:100%;height:1px;display:block}
#chat_tab_bar .chat_window .chat_input{padding:4px 4px 4px 24px;border-color:#93a2c1;border-left:none;border-right:none;border-bottom:none;background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat 4px -166px !important;margin:0}
#presence #buddy_list_tab .spritemap_icons.sx_icons_buddy_list{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat scroll -631px -101px !important;width:16px !important;margin-right:4px}
#presence.buddy_list_hidden #buddy_list_tab .spritemap_icons.sx_icons_buddy_list{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat scroll -647px -101px !important}
#buddy_list_panel a.panel_item .panel_icon{background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-repeat:no-repeat;display:block;float:left;height:8px;margin:2px 3px 0 5px;overflow:hidden;width:8px}
.buddy_list ul li .online_status_container a{background-color:#fff;background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-repeat:no-repeat;background-position:-663px -101px;cursor:pointer;display:block;height:8px;width:17px;padding:0}
.buddy_list .buddy_list_typeahead .inputtext{padding-left:17px;background:white url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat left 4px !important}
#buddy_list_panel .flyout a .menu_icon{background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-repeat:no-repeat;display:block;height:8px;float:left;margin:5px 0 0 11px;overflow:hidden;width:8px}
.UIBeeper img.UIBeeper_Top{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat 0 -128px !important;height:4px;width:230px}
.UIBeeper img.UIBeeper_Top_Selected{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat 0 -123px !important;height:4px;width:230px}
.UIBeeper img.UIBeeper_Bottom{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat 0 -112px !important;height:9px;width:230px}
.UIBeeper img.UIBeeper_Bottom_Selected{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat 0 -101px !important;height:9px;width:230px}
#presence_ui{background:url('http://img193.imageshack.us/img193/6769/toppink.png') repeat-x 0 -24px !important;margin-left:15px;margin-right:15px;position:relative;overflow:visible ! important}
#presence_bar{background:url('http://img193.imageshack.us/img193/6769/toppink.png') repeat-x 0 -24px !important}
#presence_popin_bar{background:url('http://img193.imageshack.us/img193/6769/toppink.png') repeat-x 0 -24px !important}
#presence_bar #presence_notifications_count strong{color:#fff;position:absolute;top:-4px;right:0;padding:1px 0 0;height:16px;width:17px;font-size:9px;font-weight:normal;background-image:url('http://img193.imageshack.us/img193/6769/toppink.png') !important;background-position:-801px -66px;background-repeat:no-repeat;text-align:center}
#universal_search #universal_search_input #q{background:white url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat 0 -65px !important;border:1px solid #315091;border-right:0;float:left;margin:0;padding:3px 4px;width:143px}
#universal_search #universal_search_submit a span{background:#ee83d6 url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -429px -65px !important;display:block;margin:0;padding:3px 0}
#universal_search #universal_search_submit a:active span{background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -449px -65px !important}
.inputsearch{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat left 4px !important;padding-left:17px}
#fb_menubar{background:url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat left bottom !important;position:absolute;left:0;top:0;width:964px;z-index:1;font-size:10px;line-height:normal}
#fb_menubar #fb_menubar_logo a span{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -480px -66px !important;display:block;height:17px;width:90px}
.fb_menu_dropdown .account small {background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -606px -66px !important}
.fb_menu_dropdown .apps small{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -670px -66px !important}
.fb_menu_dropdown .privacy small{background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat -638px -66px !important}
.UICantSeeProfileIcon{width:16px;height:16px;margin:2px 6px 0 0;background:transparent url('http://img193.imageshack.us/img193/6769/toppink.png') no-repeat scroll -638px -66px !important}
.UIObject_SelectedItem, .UIFilterList_ItemRight{background-image:url('http://img27.imageshack.us/img27/3415/filterpink.gif') !important}
.UIFilterList_DragHandle{background:url('http://img27.imageshack.us/img27/3415/filterpink.gif') no-repeat !important;background-position:-16px -70px;cursor:move;display:none;height:16px;position:absolute;right:5px;top:7px;width:16px;z-index:1}
.UIFilterList_Plus, .UIMutableFilterList_Arrow{background:transparent url('http://img521.imageshack.us/img521/6205/objectpinkb.gif') no-repeat !important;display:inline-block;height:10px;margin:0 3px 0 0;padding-left:7px}
.UIPager_Button{background-image:url('http://img521.imageshack.us/img521/6205/objectpinkb.gif') !important;background-repeat:no-repeat;float:left;height:22px;width:25px}
.UIFilterList_ItemLink:hover{background-color: #DDA0DD !important}
#fb_menubar_core .fb_menu .fb_menu_title .hover, #fb_menubar_core .fb_menu .fb_menu_title a:hover {background-color: #DDA0DD !important;text-decoration: none; color: #FFFFFF !important;}
#fb_menubar .fb_menu .fb_menu_title a {color: #FFFFFF !important;}
.profile .top_bar ul.tabs li a.tab_link{background-color: #F2D3E9 !important;border-color: #F2D3E9 !important;color: #E26AC2 !important}
.profile .top_bar ul.tabs li a.tab_link:hover{background-color: #F29CDB !important;border-color: #F29CDB !important}
.UIActionButton_BlueBack .UIActionButton_Text {background-color: #F29CDB !important}
.UIActionButton_BlueBack .UIActionButton_Wrap {border-color: #E101A5 !important}
.profile_actions a:hover {background-color: #F6D0EC !important}
#fb_menubar_aux .fb_menu .fb_menu_title .hover, #fb_menubar_aux .fb_menu .fb_menu_title a:hover {background-color: #DDA0DD !important;color: #FFFFFF !important}
.inputtext, .inputpassword {border-color: #F29CDB !important}
#universal_search #universal_search_input #q {border-color: #F29CDB !important}
#universal_search #universal_search_submit a {border-color: #F29CDB !important}
.inputbutton, .inputsubmit {background-color: #F29CDB !important;color: #ffffff !important;border-color: #E101A5 !important}
.PillFilter_pillfilter .PillFilter_filter.Tabset_selected div {background-color: #F29CDB !important;background-image:none !important}
.profile .box h3.box_header{background-color: #F2D3E9 !important;border-top-color: #F398DB !important}
.profile .box{border-top-color: #F398DB !important;border-right-color: #F7CFEC !important}
.toggle_tabs li a.selected {background-color: #F2D3E9 !important;border-color: #F2D3E9 !important}
.toggle_tabs li.first a.selected {border-color: #F2D3E9 !important}
.fb_menu_dropdown .fb_menu_item a:hover{background-color: #F2D3E9 !important;border-color: #D84FB3 !important}
.fb_menu_dropdown{border-color: #D84FB3 !important}
}
