css:
@-moz-document domain(ncatlab.org) {

/* 
    Hopefully I'm not missing any edge-cases. Very simple implementation of a dark mode nlab. 
    Jack Crawford 2020
*/

html {
    background-color:#151515;
}
body {
    background-color:#151515;
    color:#f5f5f5;
}
a {
    color: #6e6ac9;
}
a[href^="http://"] {
    color:#5485e7;
}
a[href^="https://"] {
    color:#5485e7;
}
a.existingWikiWord {
    color:#059f48;
}

h1, h2, h3, h4, h5, h6 {
    color: #bfbebe;
}
:target {
        background-color:#2b2b2b;
}
svg {
    filter: invert(100%);
}

span {
    color: #fff!important;
}

.newWikiWord {
    background-color:#525252;
}

div.query {
    background-color:#272d25;
    border-color:#37540c;
}

div.standout {
    background-color : #4a4a4a;
}
}
