css:
@-moz-document url-prefix("https://rezka.ag") {
body{
  min-width: 0 !important;
  color: #fff;
}
#wrapper,
.b-wrapper,
#footer{
  width: 100% !important;
}
#main,
body.active-brand .comments-form,
#hd-comments-list>.comments-tree-list>li,
#hd-comments-list>.comments-tree-list>div,
body.active-brand #hd-comments-list>.comments-tree-list .comments-tree-list,
#hd-comments-list>.comments-tree-list .comments-tree-item,
.b-comment__admin{
  background: #181818 !important;
}
.b-tophead_wrapper,
#top-nav,
.b-search__field,
body.has-brand .b-post__schedule_table .current-episode,
body.b-theme__template__night.has-brand .b-post__schedule_table .current-episode,
.b-post__schedule_table tr:hover td,
.b-addcomment .b-field,
.b-post__schedule_block_title,
.b-footer,
.b-topnav__sub_inner,
.tooltipster-base,
.b-search__live{
  background: #202020 !important;
}
.b-content__htitle,
.b-content__main_filters_link,
.b-content__main_filters_link.active,
.b-navigation a,
.b-navigation span,
.b-content__inline_item .info,
.b-content__inline_item-link a,
.b-topnav__item-link,
.b-search__field,
.b-post__info,
.b-post__info .l,
.b-post__info_rates,
.b-post__info_rates a,
.b-post__description_title,
.b-post__description_text,
.b-post__mixedtext,
.b-post__schedule_table,
.b-field,
.b-comment,
.b-comment .name{
  color: #fff !important;
}
.b-post__info a,
.persons-list-holder .person-name-item a,
.persons-list-holder .person-name-item.showing-content a,
body.b-theme__template__night .persons-list-holder .person-name-item a,
body.b-theme__template__night .persons-list-holder .person-name-item.showing-content a,
.b-comment .info,
a,
.b-comment__quoteuser,
.b-comment__like_it{
  color: #aaa !important;
}
.b-content__inline_item .cat,
.b-content__inline_item .trailer,
#tg-info-block-exclusive,
.b-post__social_holder_wrapper,
.b-post__support_holder,
.b-post__lastepisodeout,
.b-post__status__tracker__download,
.b-post__status_wrapper,
#vk_groups,
.b-content__main>.b-post__mixedtext,
#top-head{
  display: none !important;
}
.persons-list-holder .person-name-item a,
.b-comment__quoteuser,
.b-comment__like_it i{
  border-color: #aaa !important;
}
#top-nav{
  border: none;
}
.b-content__inline_item-link a{
  text-decoration: none !important;
  font-weight: bold;
}
.b-wrapper{
  padding: 0 20px !important;
  box-sizing: border-box;
}
.b-content__inline_inner_mainprobar{
  margin-left: 0;
  padding-right: 0;
}
.b-content__inline_item-cover{
  border: none;
  background: none !important;
  padding:0;
}
.b-content__inline_items{
  float: initial !important;
  display: flex;
  flex-wrap: wrap;
  position: relative;
  padding-bottom: 45px;
}
.b-content__inline_inner_mainprobar .b-content__inline_item{
  margin-left: 0 !important;
}
.b-content__inline_item-cover img{
  width: 100%;
  height: auto;
}
.b-content__inline_item .info{
  background-color: rgba(0,0,0,.8) !important;
  border-radius: 0;
}
.b-content__inline_item{
  margin-bottom: 15px !important;
  color: #aaa;
  font-size: 14px;
  font-weight: initial;
}
.b-content__inline_item .b-content__inline_item-link>*{
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  display: block;
}
.b-navigation{
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
  border: none !important;
  margin: 0 !important;
  padding: 0 !important;
  white-space: nowrap;
}

