  background-image: url("data:image/svg+xml,%3C%3Fxml version='1.0' encoding='UTF-8'%3F%3E%3Csvg width='14px' height='14px' viewBox='0 0 14 14' version='1.1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink'%3E%3Cg id='Page-1' stroke='none' stroke-width='1' fill='none' fill-rule='evenodd'%3E%3Cg id='Catalog-pages' transform='translate(-899.000000, -106.000000)' fill='%23FFFFFF' fill-rule='nonzero'%3E%3Cpath d='M904.80687,106 C901.613091,106 899,108.607324 899,111.801447 C899,114.995571 901.613091,117.608945 904.80687,117.608945 C906.034781,117.608945 907.171959,117.215729 908.109527,116.562385 L911.248866,119.696014 C911.648088,120.101329 912.301361,120.101329 912.700583,119.696014 C913.099806,119.296748 913.099806,118.649454 912.700583,118.250189 L909.561244,115.110511 C910.220566,114.166793 910.607691,113.029491 910.607691,111.801447 C910.607691,108.607324 907.994599,106 904.80687,106 Z M904.80687,108.044723 C906.887665,108.044723 908.55714,109.714378 908.55714,111.801447 C908.55714,113.888517 906.887665,115.558172 904.80687,115.558172 C902.720026,115.558172 901.050551,113.888517 901.050551,111.801447 C901.050551,109.714378 902.720026,108.044723 904.80687,108.044723 Z' id='Shape'%3E%3C/path%3E%3C/g%3E%3C/g%3E%3C/svg%3E");
}
.b-content__columns{
  padding-right: 0 !important;
}
#player{
  margin: 0 -20px;
  padding-top: 0;
}
#cdnplayer-container,
#cdnplayer{
  width: initial !important;
  height: initial !important;
}
#cdnplayer{
  position: relative;
}
#cdnplayer:before{
  content: '';
  display: block;
  width: 100%;
  padding-top: calc(100%/1.77);
}
.b-simple_seasons__list,
.b-simple_episodes__list{
  padding: 10px 20px;
}
.b-sidecover{
  padding: 0;
  border: none;
  border-radius: 4px;
  overflow: hidden;
}
.b-post__rating_table{
  margin: 20px 0;
}
.b-post__schedule_table td{
  border-top: 0 !important;
}
.b-post__schedule_block_title{
  position: relative;
}
.b-post__schedule_block_title .title{
  width: initial;
  display: inline-block;
}
.b-post__schedule_block_title .act{
  margin: 0 !important;
  padding: 8px 18px;
  position: absolute;
  top: 0;
  right: 0;
  background-color: inherit;
}
.b-addcomment td{
  padding: 0;
}
.b-addcomment .b-field{
  border: none !important;
}
.ava {
	background: rgba(255,255,255,0.05);
}
.b-footer{
  padding: 10px 0;
}
.b-content__htitle{
  padding-top: 20px;
}


}

@-moz-document url-prefix("https://rezka.ag") {
/* Extra small devices (portrait phones, less than 576px) */
@media (max-width: 575.98px) { 
  .b-content__inline_item{
    flex: 1 0 calc(100%/1 - 10px);
  }
  .b-content__inline_item:not(:nth-child(1n)){
    margin-right: 10px !important;
  }
}

/* Small devices (landscape phones, 576px and up) */
@media (min-width: 576px) and (max-width: 767.98px) {
  .b-content__inline_item{
    flex: 1 0 calc(100%/2 - 10px);
  }
  .b-content__inline_item:not(:nth-child(2n)){
    margin-right: 10px !important;
  }
}

/* Medium devices (tablets, 768px and up) */
@media (min-width: 768px) and (max-width: 991.98px) {
  .b-content__inline_item{
    flex: 1 0 calc(100%/3 - 10px);
  }
  .b-content__inline_item:not(:nth-child(3n)){
    margin-right: 10px !important;
  }
}

/* Large devices (desktops, 992px and up) */
@media (min-width: 992px) and (max-width: 1199.98px) {
  .b-content__inline_item{
    flex: 1 0 calc(100%/4 - 10px);
  }
  .b-content__inline_item:not(:nth-child(4n)){
    margin-right: 10px !important;
  }
}

/* Extra large devices (large desktops, 1200px and up) */
@media (min-width: 1200px) {
  .b-content__inline_item{
    flex: 1 0 calc(100%/6 - 10px);
  }
  .b-content__inline_item:not(:nth-child(6n)){
    margin-right: 10px !important;
  }
}
}
