css:
@-moz-document domain("www.lightnovelpub.com") {
  	::root{
  		--Blumpkin:#F5F5DC;
    	--FunkyPurpleUnicorn:#9400D3;
 	}
	#FlyingCoffeeMakingUnicorn{
		content: 'I am a mythical creature that makes coffee and flies!';
		background-color: var(--Blumpkin);
  		color: var(--FunkyPurpleUnicorn);
	}
/*This is stupid code, it doesn't do anything, this id doesn't even exist*/
}
@-moz-document domain("www.lightnovelpub.com", "www.lightnovelworld.com", "www.lightnovelspot.com") {
/*[[darkmode]]*/
.closebtn{
    display: flex !important;
}
.adsbox{
    display: none !important;
}
.ad-sticky{
    display: none !important;
}
.fs-dynamic{
    display: none !important;
}
#ad_position_box{
    display: none !important;
}
#card{
    display: none !important;
}
#sticky-footer{
    display: none !important;
}
}
@-moz-document regexp("(?:http://|https://)(?:www.|)lightnovel(?:pub|world|spot).com/account(?:/|)(?:|library|history|comments|reviews|inbox)") {
/* | Adblocker Extension | */
  .user-panel-body>div{/*[[adblock-extension]]*/}
  div.user-panel-body > div.user-panel-section{display:block;}
/* | Adblocker Extension | */
}
@-moz-document regexp("(?:http://|https://)(?:www.|)lightnovel(?:pub|world|spot).com/novel/.-*[a-zA-Z0-9].{0,}/.-*[a-zA-Z0-9].{0,}") {
/* | Adblocker Extension | */
#chapter-container > div,
#chapter-article > div.container,
#chapter-comments > section.comment-list > div,
.comment-wrapper > div,
.comment-wrapper > ul > li.comad{/*[[adblock-extension]]*/}

section.comment-list > div.head,
section.comment-list > div.comment-policy,
section.comment-list > div.comment-wrapper{display: block !important;}

/* | Adblocker Extension | */
}
@-moz-document regexp("(?:http://|https://)(?:www.|)lightnovel(?:pub|world|spot).com/hub_.-*[a-zA-Z0-9].{0,}") {
/* | Adblocker Extension | */
div.container{/*[[adblock-extension]]*/}
/* | Adblocker Extension | */
}


@-moz-document regexp("(?:http://|https://)(?:www.|)lightnovel(?:pub|world|spot).com/(?:ranking|stories|latest-updates).-*[a-zA-Z0-9].{0,}") {
  div.container > div{/*[[adblock-extension]]*/}
div.container > div.action-panel,
div.container > div.action-header,
div.container > div.paging,
div.container > div.rank-body{display:block !important;}
}

style_setting_options.value:
/* | Dark Mode On | */
:root[bgcolor=black]:root[theme=dark]{
  --bg-nav-header: var(--bg-color-2);
  --bg-color: #232323;
  --bg-color-2: #121212;
  --border-color-3: #282828;
  --anchor-color: #acacac;
  --text-color: #ddd;
  --text-color-secondary: #acacac;
}
html[theme='dark'] .booktitle,
html[theme='dark'] .chapter-title,
html[theme='dark'] #chapter-container > p:nth-child(1) > b{
  color: aquamarine;
  opacity: 0.9;
}
html[theme='dark'] .button{
  background-image: linear-gradient(180deg,#acacac26,#acacac00);
}
:root[hgcolor=purple]:root[theme=dark]{
  --bg-color-secondary-2: #484848;
  --bg-color-secondary-4: #585858;
  --bg-color-secondary-3: #acacaccc;
  --bg-color-secondary: #acacac35;
  --button-bg: #000;
  --button-secundary-border: #484848;
}
/* | Dark Mode On | */
