/* i really want this to be global */
.theme-dark {
    --header-primary: #fff;
    --header-secondary: #9f4ce0;
    --text-normal: #dcddde;
    --text-muted: #72767d;
    --text-link: #9f00ff;
    --channels-default: #8e9297;
    --interactive-normal: #606060;
    --interactive-hover: #808080;
    --interactive-active: #aa50ff;
    --interactive-muted: #aa50ff;
    --background-primary: #000;
    --background-secondary: #000;
    --background-secondary-alt: #040404;
    --background-tertiary: #020005;
    --background-accent: #000;
    --background-floating: #000;
    --background-mobile-primary: #000;
    --background-mobile-secondary: #000;
    --background-modifier-hover: rgba(100,0,255,0.2);
    --background-modifier-active: rgba(100,0,255,0.4);
    --background-modifier-selected: rgba(100,0,255,0.2);
    --background-modifier-accent: rgba(40,40,40,0.4);
    --background-mentioned: rgba(100,0,250,0.1);
    --background-mentioned-hover: rgba(100,0,250,0.15);
    --background-message-hover: rgba(20,20,20,0.3);
    --background-help-warning: rgba(100,0,255,0.4);
    --background-help-info: rgba(100,0,255,0.4);
    --scrollbar-thin-thumb: #400080;
    --scrollbar-thin-track: transparent;
    --scrollbar-auto-thumb: #400080;
    --scrollbar-auto-track: #000000;
    --scrollbar-auto-scrollbar-color-thumb: #400080;
    --scrollbar-auto-scrollbar-color-track: #000000;
    --elevation-stroke: 0 0 0 1px rgba(100,0,250,0.2);
    --elevation-low: 0 1px 0 rgba(50,50,50,0.5),0 1.5px 0 rgba(50,50,50,0.1),0 2px 0 rgba(50,50,50,0.1);
    --elevation-medium: 0 4px 4px rgba(50,0,250,0.1);
    --elevation-high: 0 8px 16px rgba(100,0,250,0.15);
    --logo-primary: #ff0000;
    --focus-primary: #000000;
    --radio-group-dot-foreground: #000000;
    --guild-header-text-shadow: 0 1px 1px rgba(0,0,0,0.4);
    --channeltextarea-background: #050505;
    --activity-card-background: #000;
    --textbox-markdown-syntax: #fff;
    --deprecated-card-bg: rgba(32,34,37,0.6);
    --deprecated-card-editable-bg: rgba(32,34,37,0.3);
    --deprecated-store-bg: #000;
    --deprecated-quickswitcher-input-background: #000;
    --deprecated-quickswitcher-input-placeholder: hsla(0,100%,0%,0.05);
    --deprecated-text-input-bg: rgba(0,0,0,0.1);
}

